import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await axios.post('http://localhost:8000/usuarios/login', { email, contrasena });
      console.log("Respuesta del servidor:", response.data); // Para depuración
      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        console.log("Token guardado en localStorage"); // Para depuración
        const from = location.state?.from || '/';
        console.log("Redirigiendo a:", from); // Para depuración
        navigate(from);
      } else {
        setError('No se recibió un token válido del servidor');
      }
    } catch (err) {
      console.error("Error durante el login:", err); // Para depuración
      setError(err.response?.data?.message || 'Error al iniciar sesión');
    }
  };

  // ... resto del componente (formulario, etc.)
};

export default Login;
