const handleLogin = async (e) => {
  e.preventDefault();
  setIsLoading(true);
  setError('');

  try {
    const success = await login(credentials.email, credentials.contrasena);
    if (success) {
      console.log('Token guardado en localStorage');
      const reservaAction = localStorage.getItem('reservaAction');
      if (reservaAction) {
        const { action, id_libro } = JSON.parse(reservaAction);
        localStorage.removeItem('reservaAction');
        navigate(`/${action}/${id_libro}`);
      } else {
        navigate('/');
      }
    } else {
      setError('Credenciales inválidas');
    }
  } catch (error) {
    console.error('Error de inicio de sesión:', error);
    setError('Error de inicio de sesión. Por favor, verifica tus credenciales.');
  } finally {
    setIsLoading(false);
  }



  return (
    <div className="container mt-5">
      <h1>Iniciar Sesión</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleLogin}>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email:</label>
          <input 
            type="email" 
            className="form-control"
            id="email"
            name="email" 
            value={credentials.email} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Contraseña:</label>
          <input 
            type="password" 
            className="form-control"
            id="password"
            name="password" 
            value={credentials.contrasena} 
            onChange={handleChange} 
            required 
          />
        </div>
        <button type="submit" className="btn btn-primary" disabled={isLoading}>
          {isLoading ? 'Cargando...' : 'Iniciar Sesión'}
        </button>
      </form>
    </div>
  );
};

export default Login;
