import React from 'react';
import { useParams } from 'react-router-dom';

const ReservaForm = () => {
    const { id_libro } = useParams(); // Extraer el id_libro de los parámetros de la ruta
    console.log('ID del libro:', id_libro); // Esto debería mostrar el ID correcto

    return (
        <div>
            <h1>Formulario de Reserva</h1>
            <p>ID del libro a reservar: {id_libro}</p> {/* Mostrar ID */}
            {/* Resto de tu formulario */}
        </div>
    );
};

export default ReservaForm;
