import React, { useState, useEffect } from 'react'; 
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const CompResultadosBusqueda = () => {
  const { termino } = useParams();
  const [libros, setLibros] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLibros = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/libros/buscar/${termino}`);
        setLibros(response.data);
      } catch (err) {
        setError('Error al cargar los libros.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchLibros();
  }, [termino]);

  const handleAction = (action, id_libro) => {
    const token = localStorage.getItem('token');
  
    if (!token) {
      // Si no hay token, almacena la acción y el ID del libro en sessionStorage
      sessionStorage.setItem('reservaAction', JSON.stringify({ action, id_libro }));
      navigate('/login');
    } else {
      // Si hay token, redirige al formulario correspondiente
      navigate(`/${action}/${id_libro}`);
    }
  };

  // Verifica el estado de carga
  if (isLoading) {
    return <div className="container mt-4">Cargando resultados...</div>;
  }

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Resultados de Búsqueda {termino ? `para "${termino}"` : ''}</h1>
      {error && <div className="alert alert-danger" role="alert">{error}</div>}

      {libros.length > 0 ? (
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {libros.map((libro) => (
            <div key={libro.id_libro} className="col">
              <div className="card h-100">
                <div className="card-body">
                  <h5 className="card-title">{libro.titulo}</h5>
                  <p className="card-text">
                    <strong>Autor:</strong> {libro.autor}<br />
                    <strong>Categoría:</strong> {libro.categoria}
                  </p>
                  <div className="btn-group" role="group">
                    <button 
                      type="button" 
                      className="btn btn-primary" 
                      onClick={() => handleAction('reservar', libro.id_libro)}
                    >
                      Reservar
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-primary" 
                      onClick={() => handleAction('prestamo', libro.id_libro)}
                    >
                      Préstamo
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-muted">
          {termino ? `No se encontraron libros para "${termino}"` : 'Realiza una búsqueda para ver resultados.'}
        </p>
      )}
    </div>
  );
};

export default CompResultadosBusqueda;
