import React from "react";

import axios from "axios"
import { useState } from "react"
import { useNavigate } from "react-router-dom"

const URI = 'http://localhost:8000/libros'

const CompCatalogo = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate(); // Hook para redirigir a otra ruta

  const handleSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim() !== '') {
      navigate(`/resultados/${searchTerm}`); // Redirige a la página de resultados
    }
  };

  return (
    <div>
      <h1>Catálogo de Libros</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Buscar por título, categoría o autor..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit">Buscar</button>
      </form>
    </div>
  );
};

export default CompCatalogo;
