import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { isAuthenticated } from './Autenticación/auth.js';

const ReservaForm = ({ id_libro }) => {
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated()) {
      console.log('Usuario no autenticado, redirigiendo a login...');
      navigate('/login');
    } else {
      console.log(`ID del libro: ${id_libro}`);
      // Aquí puedes continuar con la lógica del formulario
    }
  }, [navigate, id_libro]); // Agrega las dependencias necesarias

  return (
    <div>
      {/* Tu formulario de reserva aquí */}
    </div>
  );
};

export default ReservaForm;
