// ResultadosBusqueda.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CompResultadosBusqueda = () => {
  const { termino } = useParams(); // Obtiene el término de búsqueda desde la URL
  const [libros, setLibros] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Llama a la API para buscar libros
        const response = await axios.get(`http://localhost:8000/libros/buscar/${termino}`);
        setLibros(response.data); // Actualiza el estado con los libros encontrados
        setError(''); // Resetea el error si la búsqueda fue exitosa
      } catch (err) {
        setError('No se encontraron libros');
        setBooks([]); // Limpia la lista de libros si ocurre un error
      }
    };

    fetchData();
  }, [termino]);

  return (
    <div>
      <h1>Resultados de Búsqueda</h1>
      {error && <p>{error}</p>}

      {libros.length > 0 ? (
        <ul>
          {libros.map((libro) => (
            <li key={libro.id_libr}>
              <strong>Título:</strong> {libro.titulo} <br />
              <strong>Autor:</strong> {libro.autor} <br />
              <strong>Categoría:</strong> {libro.categoria}
            </li>
          ))}
        </ul>
      ) : (
        <p>No hay libros para mostrar</p>
      )}
    </div>
  );
};

export default CompResultadosBusqueda;
