import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CompResultadosBusqueda = () => {
  const { termino } = useParams();
  const [libros, setLibros] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        console.log('Buscando libros con término:', termino);
        const response = await axios.get(`http://localhost:8000/libros/buscar/${termino}`);
        console.log('Respuesta de la API:', response.data);
        
        if (Array.isArray(response.data)) {
          setLibros(response.data);
        } else if (response.data.message) {
          setError(response.data.message);
          setLibros([]);
        } else {
          throw new Error('Respuesta inesperada de la API');
        }
      } catch (err) {
        console.error('Error al buscar libros:', err);
        setError(err.response?.data?.message || err.message);
        setLibros([]);
      } finally {
        setIsLoading(false);
      }
    };
  
    fetchData();
  }, [termino]);

  if (isLoading) {
    return <div>Cargando resultados...</div>;
  }

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Resultados de Búsqueda para "{termino}"</h1>
      {error && <div className="alert alert-danger" role="alert">{error}</div>}

      {libros.length > 0 ? (
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {libros.map((libro) => (
            <div key={libro.id_libro} className="col">
              <div className="card h-100">
                <div className="card-body">
                  <h5 className="card-title">{libro.titulo}</h5>
                  <p className="card-text">
                    <strong>Autor:</strong> {libro.autor}<br />
                    <strong>Categoría:</strong> {libro.categoria}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-muted">No se encontraron libros para "{termino}"</p>
      )}
    </div>
  );
};

export default CompResultadosBusqueda;
