import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({ ...prev, [name]: value }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true); // Indica que la solicitud está en curso
    setError(''); // Limpia cualquier error previo

    try {
      // Realiza la llamada a la API para iniciar sesión
      const response = await axios.post('http://localhost:8000/login', credentials);
      
      // Guarda el token en localStorage
      localStorage.setItem('token', response.data.token); 

      // Verifica si hay una acción almacenada
      const reservaAction = localStorage.getItem('reservaAction');
      if (reservaAction) {
        const { action, id_libro } = JSON.parse(reservaAction);
        localStorage.removeItem('reservaAction'); // Limpia la acción almacenada

        // Redirige al formulario de reserva
        navigate(`/reservar/${id_libro}`);
      } else {
        navigate('/'); // Redirige a la página principal si no hay acción
      }
    } catch (error) {
      console.error('Error de inicio de sesión:', error);
      setError('Error de inicio de sesión. Por favor, verifica tus credenciales.'); // Muestra un mensaje de error
    } finally {
      setIsLoading(false); // Finaliza el estado de carga
    }
  };

  return (
    <div>
      <h1>Iniciar Sesión</h1>
      {error && <div className="alert alert-danger">{error}</div>} {/* Muestra errores si existen */}
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label>
          <input 
            type="email" 
            name="email" 
            value={credentials.email} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div>
          <label>Contraseña:</label>
          <input 
            type="password" 
            name="password" 
            value={credentials.password} 
            onChange={handleChange} 
            required 
          />
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Cargando...' : 'Iniciar Sesión'}
        </button>
      </form>
    </div>
  );
};

export default Login;
