import axios from 'axios'
import {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'

const URI = 'http://localhost:8000/libros'

const CompShowLibros = () => {
    const [libros, setLibro] = useState([])

    useEffect(() => {
        getLibros()
    }, [])

    const getLibros = async () => {
        const res = await axios.get(URI)
        setLibro(res.data)
    }

    const deleteLibro = async (id_libro) => {
        const token = localStorage.getItem("token"); 
        try {
            await axios.delete(`${URI}/${id_libro}`, {
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            });
            getLibros();
        } catch (error) {
            console.error("Error al eliminar el libro:", error);
            if (error.response) {
                console.log("Código de estado:", error.response.status);
                console.log("Mensaje de error:", error.response.data);
            }
        }
    }

    return (
        <div className='container mt-5'>
            <div className='row'>
                <div className='col'>
                    <table className='table table-hover table-bordered' style={{ backgroundColor: 'white' }}>
                    <thead className='text-center' style={{ backgroundColor: '#8B4513', color: '#A0522D' }}> {/* Color café */}

                            <tr>
                                <th>Título</th>
                                <th>Autor</th>
                                <th>ISBN</th>
                                <th>Categoría</th>
                                <th>Cantidad</th>
                                <th>Disponible</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {libros.map((libro) => (
                                <tr key={libro.id_libro}>
                                    <td>{libro.titulo}</td>
                                    <td>{libro.autor}</td>
                                    <td>{libro.isbn}</td>
                                    <td>{libro.categoria}</td>
                                    <td>{libro.cantidad}</td>
                                    <td>{libro.disponible ? "Sí" : "No"}</td>
                                    <td className="text-center">
                                        <div className="btn-group" role="group">
                                            <Link to="/create" className='btn btn-primary btn-sm' style={{ backgroundColor: '#007bff', color: 'white', marginRight: '5px' }}>Crear</Link>
                                            <Link to={`/edit/${libro.id_libro}`} className='btn btn-success btn-sm' style={{ backgroundColor: '#28a745', color: 'white', marginRight: '5px' }}>Editar</Link>
                                            <button onClick={() => deleteLibro(libro.id_libro)} className='btn btn-danger btn-sm' style={{ backgroundColor: '#dc3545', color: 'white' }}>Eliminar</button>
                                        </div>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}

export default CompShowLibros
