import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

const RestablecerContrasena = () => {
  const { token } = useParams(); // Obtener el token de la URL
  const history = useHistory();
  const [nuevaContrasena, setNuevaContrasena] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Verificar el token cuando el componente se monta
  useEffect(() => {
    axios.get(`http://localhost:8000/restablecer/${token}`)
      .then(response => {
        setMensaje(response.data.message); // Si el token es válido, mostramos un mensaje
      })
      .catch(err => {
        setError('El token no es válido o ha expirado'); // Si el token no es válido, mostramos un error
      });
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      // Enviar el token y la nueva contraseña al backend
      const response = await axios.post(`http://localhost:8000/actualizar/${token}`, { nuevaContrasena });
      setMensaje(response.data.message); // Mostrar mensaje de éxito

      // Redirigir al usuario al login después de un tiempo
      setTimeout(() => {
        history.push('/login');
      }, 2000);
    } catch (err) {
      setError(err.response?.data?.message || 'Error al actualizar la contraseña'); // Manejar errores
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="restablecer-container">
      <h2>Restablecer Contraseña</h2>
      
      {error && <p>{error}</p>}
      {mensaje && <p>{mensaje}</p>}

      {!error && !mensaje && (
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nueva Contraseña</label>
            <input
              type="password"
              value={nuevaContrasena}
              onChange={(e) => setNuevaContrasena(e.target.value)}
              required
            />
          </div>
          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Actualizando...' : 'Actualizar Contraseña'}
          </button>
        </form>
      )}
    </div>
  );
};

export default RestablecerContrasena;
