import React, { useState } from 'react';
import axios from 'axios';

const ActualizarPerfil = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(`http://localhost:8000/usuarios/actualizar`, 
        { nombre, email }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      setMensaje(response.data.message); // Mensaje de éxito
    } catch (error) {
      console.error('Error al actualizar perfil:', error.response ? error.response.data : error.message);
      setMensaje(error.response?.data?.message || 'Error al actualizar perfil');
    }
  };

  return (
    <div>
      <h2>Actualizar Perfil</h2>
      {mensaje && <p>{mensaje}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <button type="submit">Actualizar Perfil</button>
      </form>
    </div>
  );
};

export default ActualizarPerfil;
