import axios from 'axios';
import { useState } from 'react';

const URI = 'http://localhost:8000/usuarios';

const CompCreateUsuario = () => {
    const [nombre, setNombre] = useState('');
    const [email, setEmail] = useState('');
    const [contrasena, setContrasena] = useState('');
    const [idRol, setIdRol] = useState(null); 

    const store = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token"); 
            await axios.post(
                URI, 
                { 
                    nombre, 
                    email, 
                    contrasena, 
                    id_rol: idRol 
                }, 
                {
                    headers: {
                        "Authorization": `Bearer ${token}`, 
                        "Content-Type": "application/json" 
                    }
                }
            );
            alert("Usuario creado con éxito");
        } catch (error) {
            console.error("Error al crear el usuario:", error);
            if (error.response) {
                console.log("Código de estado:", error.response.status);
                console.log("Mensaje de error:", error.response.data);
            }
        }
    };

    return (
        <div className='cont-create-usuario'>
            <h1 className="titulo-crear-usuario">Crear Usuario</h1>
            <form onSubmit={store}>
                <div className='mb-3'>
                    <label className='form-label'>Nombre</label>
                    <input
                        type='text'
                        className='form-control'
                        value={nombre}
                        onChange={(e) => setNombre(e.target.value)}
                        required
                    />
                </div>
                <div className='mb-3'>
                    <label className='form-label'>Email</label>
                    <input
                        type='email'
                        className='form-control'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className='mb-3'>
                    <label className='form-label'>Contraseña</label>
                    <input
                        type='password'
                        className='form-control'
                        value={contrasena}
                        onChange={(e) => setContrasena(e.target.value)}
                        required
                    />
                </div>
                <div className='mb-3'>
                    <label className='form-label'>ID de Rol (Opcional)</label>
                    <input
                        type='number'
                        className='form-control'
                        value={idRol || ''} 
                        onChange={(e) => setIdRol(e.target.value)}
                    />
                </div>
                <button type='submit' className='btn-guardar'>
                    Crear
                </button>
            </form>
        </div>
    );
};
export default CompCreateUsuario;
