import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import './Login.css'; 
import logo from './assets/logo.jpeg'; 

const Login = () => {
  const [email, setEmail] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Limpiar error previo
    setIsLoading(true); // Activar el estado de carga
  
    try {
      // Realizamos la solicitud POST al backend para autenticar el usuario
      const response = await axios.post('http://localhost:8000/usuarios/login', {
        email,
        contrasena
      });
  
      // Si el login es exitoso, guardamos el token en localStorage
      localStorage.setItem('token', response.data.token);
  
      // Leer la URL de destino y redirigir
      const redirectTo = localStorage.getItem('redirectAfterLogin');
      if (redirectTo) {
        navigate(redirectTo);
        localStorage.removeItem('redirectAfterLogin'); // Elimina la URL después de redirigir
      } else {
        // Si no hay URL de destino, redirigir al dashboard o página principal
        navigate('/home'); 
      }
    } catch (err) {
      // Manejar errores de login
      setError(err.response?.data?.message || 'Credenciales incorrectas. Inténtalo de nuevo.');
    } finally {
      setIsLoading(false); // Desactivar el estado de carga
    }
  };
  

  return (
    <div className="login-wrapper">
      <div className="login-container">
        <h2>Bienvenido a la Biblioteca Pública<br />Mario Hugo Marino Ortiz</h2>
        <img src={logo} alt="Logo Biblioteca" className="logo-image" />

        {/* Mostrar errores si existen */}
        {error && <div className="alert alert-danger">{error}</div>}

        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Correo Electrónico:</label>
            <input
              type="email"
              id="email"
              className="form-control"
              placeholder="Ingrese su correo"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="contrasena">Contraseña:</label>
            <input
              type="password"
              id="contrasena"
              className="form-control"
              placeholder="Ingrese su contraseña"
              value={contrasena}
              onChange={(e) => setContrasena(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary" disabled={isLoading}>
            {isLoading ? 'Cargando...' : 'Iniciar sesión'}
          </button>
        </form>

        <div className="mt-3 text-center">
          <Link to="/registro">¿No tienes cuenta? Regístrate aquí</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
