import React, { useState, useEffect, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import logo from './assets/logo.jpeg';
import perfilIcon from './assets/perfil.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css';
import MenuLateral from './MenuLateral.js';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const isAdmin = localStorage.getItem('isAdmin') === 'true';
  const [nombreUsuario, setNombreUsuario] = useState('Invitado');
  const [menuLateralAbierto, setMenuLateralAbierto] = useState(false);
  const [isNavCollapsed, setIsNavCollapsed] = useState(true);

  const menuLateralRef = useRef(null); // Referencia al menú lateral

  // Función para alternar la visibilidad del menú lateral
  const toggleMenuLateral = () => {
    setMenuLateralAbierto(!menuLateralAbierto); 
  };

  // Función para manejar el colapso del menú de navegación
  const handleNavCollapse = () => setIsNavCollapsed(!isNavCollapsed);

  // Hook para establecer el nombre del usuario desde el localStorage
  useEffect(() => {
    const nombre = localStorage.getItem('nombre');
    if (nombre) {
      setNombreUsuario(nombre);
    }
  }, []); // Este hook solo se ejecuta al montar el componente

  // Hook para actualizar el nombre de usuario cuando cambia en el localStorage
  useEffect(() => {
    const handleStorageChange = () => {
      const nombre = localStorage.getItem('nombre');
      if (nombre) {
        setNombreUsuario(nombre);
      } else {
        setNombreUsuario('Invitado');
      }
    };

    window.addEventListener('storage', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  // Hook para cerrar el menú lateral si se hace clic fuera del menú
  useEffect(() => {
    const handleClickOutside = (event) => {
      // Verificar si el clic ocurrió fuera del menú lateral
      if (menuLateralRef.current && !menuLateralRef.current.contains(event.target)) {
        setMenuLateralAbierto(false); // Cerrar el menú si se hizo clic fuera
      }
    };

    // Añadir el evento al document
    document.addEventListener("click", handleClickOutside);

    // Limpiar el evento cuando el componente se desmonte
    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);


  // Función para manejar el cierre de sesión
  const handleLogout = () => {
    localStorage.clear();
    setNombreUsuario('Invitado');
    navigate('/login');
  };

  // Función para manejar el clic en el perfil
  const handleProfileClick = () => {
    navigate('/configuracion-perfil');
  };

  // Función para verificar si la ruta está activa
  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg custom-navbar">
        <div className="container-fluid">
          <div className="navbar-brand d-flex align-items-center">
            <img src={logo} alt="Logo" className="profile-logo me-2" />
            <span className="navbar-brand-title">Mario Hugo</span>
          </div>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded={!isNavCollapsed ? true : false}
            aria-label="Toggle navigation"
            onClick={handleNavCollapse}
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className={`${isNavCollapsed ? 'collapse' : ''} navbar-collapse`} id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a 
                  className={`nav-link ${isActive('/inicio') ? 'active' : ''}`} 
                  href="/inicio"
                >
                  Inicio
                </a>
              </li>
              <li className="nav-item">
                <a 
                  className={`nav-link ${isActive('/catalogo') ? 'active' : ''}`} 
                  href="/catalogo"
                >
                  Catálogo
                </a>
              </li>
              <li className="nav-item">
                <a 
                  className={`nav-link ${isActive('/historial') ? 'active' : ''}`} 
                  href="/historial"
                >
                  Historial
                </a>
              </li>
              {isAdmin && (
                <li className="nav-item">
                  <a 
                    className={`nav-link ${isActive('/libros') ? 'active' : ''}`} 
                    href="/libros"
                  >
                    Libros
                  </a>
                </li>
              )}
              {isAdmin && (
                <li className="nav-item">
                  <a 
                    className={`nav-link ${isActive('/usuarios') ? 'active' : ''}`} 
                    href="/usuarios"
                  >
                    Usuarios
                  </a>
                </li>
              )}
            </ul>

            <div
              className="profile d-flex align-items-center"
              style={{ cursor: 'pointer' }}
              onClick={toggleMenuLateral}
            >
              <span className="profile-text me-2">{nombreUsuario}</span>
              <img src={perfilIcon} alt="Icono de Perfil" className="profile-icon" />
            </div>
          </div>
        </div>
      </nav>

      <MenuLateral
        ref={menuLateralRef}
        isOpen={menuLateralAbierto}
        toggleMenu={toggleMenuLateral}
        handleLogout={handleLogout}
        handleProfileClick={handleProfileClick}
      />
    </div>
  );
};

export default Navbar;
