import axios from 'axios'
import { useState, useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom'

const URI = 'http://localhost:8000/usuarios'

const CompEditUsuario = () => {
    const [nombre, setNombre] = useState('')
    const [email, setEmail] = useState('')
    const [id_rol, setIdRol] = useState('')  // Suponiendo que los roles están disponibles en el backend
    const { id_usuario } = useParams()
    const navigate = useNavigate()

    // Procedimiento para actualizar el usuario
    const update = async (e) => {
        e.preventDefault()
        const token = localStorage.getItem("token")
        try {
            await axios.put(`${URI}/${id_usuario}`, {
                nombre: nombre,
                email: email,
                id_rol: id_rol
            }, {
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            })
            navigate('/usuarios')
        } catch (error) {
            console.error("Error al actualizar el usuario:", error)
            if (error.response) {
                console.log("Código de estado:", error.response.status)
                console.log("Mensaje de error:", error.response.data)
            }
        }
    }

    // Obtener los datos del usuario por ID
    useEffect(() => {
        getUsuarioById()
    }, [])

    const getUsuarioById = async () => {
        const res = await axios.get(`${URI}/${id_usuario}`)
        setNombre(res.data.nombre)
        setEmail(res.data.email)
        setIdRol(res.data.id_rol)
    }

    return (
        <div>
            <h1>Editar Usuario</h1>
            <form onSubmit={update}>
                <div className="mb-3">
                    <label className="form-label">Nombre</label>
                    <input
                        value={nombre}
                        onChange={(e) => setNombre(e.target.value)}
                        type="text"
                        className="form-control"
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Email</label>
                    <input
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        type="email"
                        className="form-control"
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Rol</label>
                    <select
                        value={id_rol}
                        onChange={(e) => setIdRol(e.target.value)}
                        className="form-control"
                    >
                        <option value="">Selecciona un rol</option>
                        <option value="1">Admin</option>
                        <option value="2">Usuario</option>
                        {/* Puedes agregar más roles según los datos disponibles en tu backend */}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Actualizar</button>
            </form>
        </div>
    )
}

export default CompEditUsuario;
