import React, { useState } from 'react';
import axios from 'axios';

const ActualizarPerfil = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');
    console.log('Token:', token);  // Verifica que el token esté presente
    if (!token) {
      setError('Token no disponible. Inicia sesión primero.');
      return;
    }

    // Obtener el id_usuario del localStorage o del token decodificado si es necesario
    const id_usuario = localStorage.getItem('id_usuario');  // Asegúrate de guardar el id_usuario cuando inicies sesión
    if (!id_usuario) {
      setError('ID de usuario no disponible.');
      return;
    }

    try {
      // Cambia la URL para incluir el id_usuario como parámetro
      const response = await axios.put(
        `http://localhost:8000/usuarios/actualizar/${id_usuario}`, 
        { nombre, email },
        {
          headers: {
            'Authorization': `Bearer ${token}`  // Incluye el token en los encabezados
          }
        }
      );
      
      setMensaje(response.data.message);
      setError('');
    } catch (err) {
      setError(err.response?.data?.message || 'Error al actualizar el perfil');
      setMensaje('');
    }
  };

  return (
    <div>
      <h2>Actualizar Perfil</h2>
      {mensaje && <div style={{ color: 'green' }}>{mensaje}</div>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
      
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre</label>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <button type="submit">Actualizar</button>
      </form>
    </div>
  );
};

export default ActualizarPerfil;
