'use client'

import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

export default function CompReservaForm() {
  const { id_libro } = useParams();
  const [formData, setFormData] = useState({
    id_libro: id_libro || '',
    id_usuario: '',
    nombres: '',
    ap_paterno: '',
    ap_materno: '',
    fecha_reserva: '',
    estado: 'activo',
  });

  const [reservationSuccess, setReservationSuccess] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('id_usuario');
    if (!token || !userId) {
      navigate('/login');
    } else {
      setFormData(prev => ({ ...prev, id_usuario: userId }));
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No se encontró el token de autenticación');
      }

      console.log('Sending reservation data:', formData);

      const response = await axios.post('http://localhost:8000/api/libros/reservar', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log('Reservation response:', response.data);

      setReservationSuccess(true);
      alert(response.data.message);
    } catch (error) {
      console.error('Error al reservar el libro:', error.response || error);
      setErrorMessage(error.response?.data?.message || 'Error al reservar el libro. Por favor, intente nuevamente.');
    }
  };

  const handleGeneratePDF = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Token de autorización no encontrado. Por favor, inicie sesión nuevamente.');
      }

      const response = await axios.post(
        'http://localhost:8000/api/libros/generar-pdf-reserva',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
          responseType: 'blob',
        }
      );

      const pdfBlob = new Blob([response.data], { type: 'application/pdf' });
      const pdfUrl = window.URL.createObjectURL(pdfBlob);
      const link = document.createElement('a');
      link.href = pdfUrl;
      link.download = `reserva_${formData.id_usuario}.pdf`;
      link.click();
      window.URL.revokeObjectURL(pdfUrl);
    } catch (error) {
      console.error('Error al generar el PDF:', error.response || error);
      alert('Error al generar el PDF. Por favor, verifique la consola para más detalles.');
    }
  };

  return (
    <div className="bg-gray-100 p-6 rounded-lg shadow-md max-w-md mx-auto mt-10">
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Formulario de Reserva</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label htmlFor="nombres" className="block text-sm font-medium text-gray-700">Nombre(s)</label>
            <input
              type="text"
              id="nombres"
              name="nombres"
              value={formData.nombres}
              onChange={handleInputChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              required
            />
          </div>
          <div>
            <label htmlFor="ap_paterno" className="block text-sm font-medium text-gray-700">Apellido Paterno</label>
            <input
              type="text"
              id="ap_paterno"
              name="ap_paterno"
              value={formData.ap_paterno}
              onChange={handleInputChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              required
            />
          </div>
          <div>
            <label htmlFor="ap_materno" className="block text-sm font-medium text-gray-700">Apellido Materno</label>
            <input
              type="text"
              id="ap_materno"
              name="ap_materno"
              value={formData.ap_materno}
              onChange={handleInputChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              required
            />
          </div>
          <div>
            <label htmlFor="fecha_reserva" className="block text-sm font-medium text-gray-700">Fecha de Reserva</label>
            <input
              type="date"
              id="fecha_reserva"
              name="fecha_reserva"
              value={formData.fecha_reserva}
              onChange={handleInputChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
              required
            />
          </div>
        </div>
        <button
          type="submit"
          className="w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
        >
          Reservar
        </button>
      </form>

      {reservationSuccess && (
        <div className="mt-6 text-center">
          <p className="text-green-600 mb-4">¡La reserva ha sido generada exitosamente!</p>
          <button
            onClick={handleGeneratePDF}
            className="py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
          >
            Generar Reserva en PDF
          </button>
        </div>
      )}

      {errorMessage && (
        <div className="mt-4 text-center text-red-600">
          <p>{errorMessage}</p>
        </div>
      )}
    </div>
  );
}
