import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import './FormularioReserva.css';

const CompReservaForm = () => {
  const { id_libro } = useParams();
  const location = useLocation(); // Usamos useLocation para obtener el título
  const { titulo } = location.state || {}; // Recupera el título si se pasó en el estado

  // Estado del formulario
  const [formData, setFormData] = useState({
    titulo: titulo || '',  // Inicializa el título con el valor pasado
    id_libro: id_libro || '',
    id_usuario: '',
    nombres: '',
    ap_paterno: '',
    ap_materno: '',
    fecha_reserva: '',
    estado: 'activo',
  });

  const [reservationSuccess, setReservationSuccess] = useState(false); // Nuevo estado
  const [isModalOpen, setIsModalOpen] = useState(false); // Estado del modal
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');

      const response = await axios.post('http://localhost:8000/libros/reservar', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert(response.data.message);
      setReservationSuccess(true); // Actualiza el estado para mostrar el mensaje y el botón
      setIsModalOpen(true); // Abre el modal
    } catch (error) {
      console.error('Error al reservar el libro:', error);
      alert(error.response?.data.message || 'Error al reservar el libro');
    }
  };

  const handleGeneratePDF = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Token de autorización no encontrado. Por favor, inicie sesión nuevamente.');
        return;
      }

      const response = await axios.post(
        'http://localhost:8000/libros/generar-pdf-reserva',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
          responseType: 'blob', // Para recibir el archivo PDF en formato blob
        }
      );

      if (response.status === 200) {
        const pdfBlob = new Blob([response.data], { type: 'application/pdf' });
        const pdfUrl = window.URL.createObjectURL(pdfBlob);
        const link = document.createElement('a');
        link.href = pdfUrl;
        link.download = `reserva_${formData.id_usuario}.pdf`;
        link.click();
        window.URL.revokeObjectURL(pdfUrl); // Limpia el URL del blob
      } else {
        console.error('Error al generar el PDF: Respuesta inesperada', response);
        alert('Error al generar el PDF. Intente nuevamente.');
      }
    } catch (error) {
      console.error('Error al generar el PDF:', error.response || error);
      alert('Error al generar el PDF. Por favor, verifique la consola para más detalles.');
    }
  };

  return (
    <div className="form-reserva">
      <h2>Formulario de Reserva</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Título del Libro
          <input type="text" name="titulo" value={formData.titulo} readOnly />
        </label>
        <label>
          Nombre(s)
          <input
            type="text"
            name="nombres"
            value={formData.nombres}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Apellido Paterno
          <input
            type="text"
            name="ap_paterno"
            value={formData.ap_paterno}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Apellido Materno
          <input
            type="text"
            name="ap_materno"
            value={formData.ap_materno}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Fecha de Reserva
          <input
            type="date"
            name="fecha_reserva"
            value={formData.fecha_reserva}
            onChange={handleInputChange}
            required
          />
        </label>
        <button type="submit">Reservar</button>
      </form>

      {/* Modal de éxito de reserva */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <p>¡La reserva ha sido generada exitosamente!</p>
            <button onClick={handleGeneratePDF}>Generar PDF de Reserva</button>
            <button onClick={() => setIsModalOpen(false)}>Cerrar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CompReservaForm;
