import React, { useState, useEffect } from "react";
import axios from "axios";

const Gestion = () => {
  const [reservas, setReservas] = useState([]);  // Guardará las reservas de todos los usuarios
  const [loading, setLoading] = useState(true);  // Para mostrar un indicador de carga
  const [error, setError] = useState(null);  // Para manejar errores de la solicitud

  // Función para obtener todas las reservas
  const obtenerReservas = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("No hay token de autenticación");
        setLoading(false);
        return;
      }

      const response = await axios.get("http://localhost:8000/libros/reservas", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      // Verificamos qué está devolviendo la API
      console.log(response.data);  // Ver la respuesta completa de la API

      // Verifica si la respuesta contiene las reservas y actualiza el estado
      setReservas(response.data.reservas || []);
      setLoading(false);
    } catch (err) {
      console.error("Error al cargar las reservas:", err);
      setError("Error al cargar las reservas");
      setLoading(false);
    }
  };

  // Función para actualizar el estado de la reserva
  const handleActualizarEstado = async (id_reserva, nuevoEstado) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("No hay token de autenticación");
        return;
      }

      const response = await axios.put(
        `http://localhost:8000/libros/reservas/${id_reserva}/estado`,
        { estado: nuevoEstado },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(response.data.message);
      obtenerReservas();  // Vuelve a cargar las reservas después de actualizar el estado
    } catch (error) {
      alert("Error al actualizar el estado de la reserva");
    }
  };

  // Cargar las reservas cuando el componente se monte
  useEffect(() => {
    obtenerReservas();
  }, []);

  if (loading) {
    return <div>Cargando reservas...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Gestionar Reservas</h2>
      <table>
        <thead>
          <tr>
            <th>ID Reserva</th>
            <th>Libro</th>
            <th>Usuario</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {reservas.length === 0 ? (
            <tr>
              <td colSpan="5">No hay reservas para mostrar</td>
            </tr>
          ) : (
            reservas.map((reserva) => (
              <tr key={reserva.id_reserva}>
                <td>{reserva.id_reserva}</td>
                <td>{reserva.libro?.titulo}</td>  {/* Asumiendo que tienes un objeto libro */}
                <td>{reserva.usuario?.nombre}</td>  {/* Asumiendo que tienes un objeto usuario */}
                <td>{reserva.estado}</td>
                <td>
                  <button
                    onClick={() => handleActualizarEstado(reserva.id_reserva, "Aprobada")}
                  >
                    Aprobar
                  </button>
                  <button
                    onClick={() => handleActualizarEstado(reserva.id_reserva, "Rechazada")}
                  >
                    Rechazar
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Gestion;
