import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const URI = 'http://localhost:8000/libros';

const CompShowLibros = () => {
    const [libros, setLibro] = useState([]);

    useEffect(() => {
        getLibros();
    }, []);

    const getLibros = async () => {
        const res = await axios.get(URI);
        setLibro(res.data);
    };

    const deleteLibro = async (id_libro) => {
        const token = localStorage.getItem("token");
        try {
            await axios.delete(`${URI}/${id_libro}`, {
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            });
            getLibros();  
        } catch (error) {
            console.error("Error al eliminar el libro:", error);
            if (error.response) {
                console.log("Código de estado:", error.response.status);
                console.log("Mensaje de error:", error.response.data);
            }
        }
    };

    return (
        <div className="vista-libros-container">
          <div className='row'>
            <div className='col'>
              <Link 
                to="/create" 
                className='btn-crear-libro mb-3'
              >
                Crear Libro
              </Link>
      
              {/* Contenedor para hacer la tabla responsiva */}
              <div className="table-responsive">
                <table className='table-libros'>
                  <thead className='text-center'>
                    <tr>
                      <th>Título</th>
                      <th>Autor</th>
                      <th>ISBN</th>
                      <th>Categoría</th>
                      <th>Cantidad</th>
                      <th>Disponible</th>
                      <th>Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {libros.map((libro) => (
                      <tr key={libro.id_libro}>
                        <td>{libro.titulo}</td>
                        <td>{libro.autor}</td>
                        <td>{libro.isbn}</td>
                        <td>{libro.categoria}</td>
                        <td>{libro.cantidad}</td>
                        <td>{libro.disponible ? "Sí" : "No"}</td>
                        <td className="text-center">
                          <div className="btn-group" role="group">
                            <Link 
                              to={`/edit/${libro.id_libro}`} 
                              className='btn-editar btn-sm'
                            >
                              Editar
                            </Link>
                            <button 
                              onClick={() => deleteLibro(libro.id_libro)} 
                              className='btn-eliminar btn-sm'
                            >
                              Eliminar
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              {/* Fin del contenedor responsivo */}
            </div>
          </div>
        </div>
      );
    }
    export default CompShowLibros;
