import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css'; // Importar el CSS para estilos
import logo from './assets/logo.jpeg'; // Importar el logo

const Login = () => {
  const [email, setEmail] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

const handleSubmit = async (e) => {
  e.preventDefault();
  setError('');
  setIsLoading(true);

  try {
    const response = await axios.post('http://localhost:8000/usuarios/login', {
      email,
      contrasena
    });

    localStorage.setItem('token', response.data.token);
    localStorage.setItem('userEmail', email);

    const isAdmin = email === 'admin@gmail.com';
    localStorage.setItem('isAdmin', isAdmin); // Aquí solo guardamos si es admin

    // Redirigir a la página de inicio después del inicio de sesión exitoso
    navigate('/inicio');
  } catch (err) {
    setError(err.response?.data?.message || 'Credenciales incorrectas.');
  } finally {
    setIsLoading(false);
  }
};


  const handleRegisterClick = () => {
    navigate('/registro');
  };

  return (
    <div className="login-wrapper">
      <div className="login-container">
        <h2>Biblioteca Pública<br />Mario Hugo Marino Ortiz</h2>
        <img src={logo} alt="Logo" className="logo-image" />
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <input
              type="email"
              placeholder="Correo"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-control"
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              placeholder="Contraseña"
              value={contrasena}
              onChange={(e) => setContrasena(e.target.value)}
              className="form-control"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary" disabled={isLoading}>
            {isLoading ? 'Cargando...' : 'Iniciar sesión'}
          </button>
          <button 
            type="button" 
            className="btn btn-secondary" 
            onClick={handleRegisterClick}
          >
            Registrarse
          </button>
        </form>
        {error && <p className="message" style={{ color: 'red' }}>{error}</p>}
      </div>
    </div>
  );
};

export default Login;
