import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Historial.css";
import Modal from "./Modal"; // Importamos el Modal

const Historial = ({ id_usuario }) => {
  const [historial, setHistorial] = useState(null);
  const [error, setError] = useState(null);
  const [mensajeExito, setMensajeExito] = useState(null); // Estado para manejar el mensaje de éxito
  const [mostrarModal, setMostrarModal] = useState(false); // Controla la visibilidad del Modal
  const [mensajeModal, setMensajeModal] = useState(""); // Contenido del mensaje en el Modal
  const [tipoModal, setTipoModal] = useState(""); // Tipo de mensaje: 'exito' o 'error'

  const cerrarModal = () => setMostrarModal(false); // Cierra el modal

  // Función para cambiar el estado de una reserva
  const cambiarEstadoReserva = async (id_reserva, nuevoEstado) => {
    try {
      const response = await axios.put(
        `http://localhost:8000/libros/reservas/${id_reserva}`,
        { estado: nuevoEstado, id_usuario },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      // Actualizar el estado local después de cambiar el estado de la reserva
      setHistorial((prevHistorial) => {
        const reservasActualizadas = prevHistorial.reservas.map((reserva) =>
          reserva.id_reserva === id_reserva
            ? { ...reserva, estado: nuevoEstado }
            : reserva
        );
        return { ...prevHistorial, reservas: reservasActualizadas };
      });

      setMensajeModal("Estado de la reserva actualizado con éxito.");
      setTipoModal("exito");
      setMostrarModal(true); // Mostrar el modal
    } catch (error) {
      setMensajeModal(error.response ? error.response.data.message : error.message);
      setTipoModal("error");
      setMostrarModal(true); // Mostrar el modal
    }
  };

  // Función para devolver el libro
  const devolverLibro = async (id_prestamo) => {
    try {
      const response = await axios.put(
        `http://localhost:8000/libros/prestamos/${id_prestamo}`,
        { devuelto: true },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      // Actualizar el estado local después de marcar el libro como devuelto
      setHistorial((prevHistorial) => {
        const prestamosActualizados = prevHistorial.prestamos.map((prestamo) =>
          prestamo.id_prestamo === id_prestamo
            ? { ...prestamo, devuelto: true, fecha_devolucion: new Date().toLocaleDateString() }
            : prestamo
        );
        return { ...prevHistorial, prestamos: prestamosActualizados };
      });

      setMensajeModal("Libro marcado como devuelto con éxito.");
      setTipoModal("exito");
      setMostrarModal(true); // Mostrar el modal
    } catch (error) {
      setMensajeModal(error.response ? error.response.data.message : error.message);
      setTipoModal("error");
      setMostrarModal(true); // Mostrar el modal
    }
  };

  useEffect(() => {
    const fetchHistorial = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8000/libros/historial/${id_usuario}`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        setHistorial(response.data);
      } catch (error) {
        setError(error.response ? error.response.data.message : error.message);
      }
    };

    fetchHistorial();
  }, [id_usuario]);

  if (error) {
    return <p className="historial-error">{error}</p>;
  }

  if (!historial) {
    return <p className="historial-loading">Cargando historial...</p>;
  }

  return (
    <div className="historial-container">
      <h2 className="historial-title">HISTORIAL</h2>

      {/* Mostrar el modal si se activa */}
      {mostrarModal && (
        <Modal mensaje={mensajeModal} tipo={tipoModal} onClose={cerrarModal} />
      )}

      {historial.reservas.length > 0 ? (
        <>
          <h3 className="historial-subtitle">Reservas</h3>
          <table className="historial-table">
            <thead>
              <tr>
                <th>Libro</th>
                <th>Autor</th>
                <th>Fecha Reserva</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {historial.reservas.map((reserva, index) => (
                <tr key={index}>
                  <td>{reserva.libro.titulo}</td>
                  <td>{reserva.libro.autor}</td>
                  <td>{reserva.fecha_reserva}</td>
                  <td>
                    <select
                      className="historial-select"
                      value={reserva.estado}
                      onChange={(e) => cambiarEstadoReserva(reserva.id_reserva, e.target.value)}
                    >
                      <option value="activo">Activo</option>
                      <option value="completo">Completo</option>
                      <option value="cancelado">Cancelado</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p className="historial-empty">No tienes reservas registradas.</p>
      )}

      {historial.prestamos && historial.prestamos.length > 0 ? (
        <>
          <h3 className="historial-subtitle">Préstamos</h3>
          <table className="historial-table">
            <thead>
              <tr>
                <th>Libro</th>
                <th>Autor</th>
                <th>Fecha Préstamo</th>
                <th>Estado de Devolución</th>
                <th>Fecha Devolución</th>
                <th>Acción</th>
              </tr>
            </thead>
            <tbody>
              {historial.prestamos.map((prestamo, index) => (
                <tr key={index}>
                  <td>{prestamo.libro.titulo}</td>
                  <td>{prestamo.libro.autor}</td>
                  <td>{prestamo.fecha_prestamo}</td>
                  <td>{prestamo.devuelto ? "Devuelto" : "Pendiente"}</td>
                  <td>{prestamo.fecha_devolucion}</td>
                  <td>
                    {!prestamo.devuelto && (
                      <button
                        className="historial-devolver-btn"
                        onClick={() => devolverLibro(prestamo.id_prestamo)}
                      >
                        Devolver
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p className="historial-empty">No tienes préstamos registrados.</p>
      )}
    </div>
  );
};

export default Historial;
