import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css'; // Importar el CSS para estilos
import logo from './assets/logo.jpeg'; // Importar el logo

const Login = () => {
  const [email, setEmail] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:8000/usuarios/login', {
        email,
        contrasena,
      });

      // Almacenar datos del usuario y token en el localStorage
      const { token, usuario } = response.data;
      localStorage.setItem('token', token); // Almacena el token
      localStorage.setItem('nombre', usuario.nombre); // Almacena el nombre del usuario
      localStorage.setItem('isAdmin', usuario.email === 'admin@gmail.com'); // Verifica si es admin

      // Redirigir a la página principal
      navigate('/inicio', { state: { message: '¡Inicio de sesión exitoso!' } });
    } catch (err) {
      setError(err.response?.data?.message || 'Error al iniciar sesión');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-wrapper">
      <div className="login-container">
        <h2>Biblioteca Pública<br />Mario Hugo Marino Ortiz</h2>
        <img src={logo} alt="Logo" className="logo-image" /> {/* Logo debajo del título */}
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <input
              type="text"
              placeholder="Correo"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="form-control"
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              placeholder="Contraseña"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="form-control"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">Iniciar sesión</button>
          <button 
            type="button" 
            className="btn btn-secondary" 
            onClick={handleRegisterClick}
          >
            Registrarse
          </button>
        </form>
        {message && <p className="message">{message}</p>}
      </div>
    </div>
  );
};
export default Login;
