import React, { useState, useEffect } from 'react'; 
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const CompResultadosBusqueda = () => {
  const { termino } = useParams(); // Obtiene el término de búsqueda de la URL
  const [libros, setLibros] = useState([]); // Estado para almacenar los resultados
  const [error, setError] = useState(''); // Estado para almacenar el error
  const [isLoading, setIsLoading] = useState(true); // Estado para manejar la carga
  const navigate = useNavigate(); // Hook de navegación para redirigir

  useEffect(() => {
    const fetchLibros = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/libros/buscar/${termino}`);
        setLibros(response.data); // Almacena los libros obtenidos
      } catch (err) {
        setError('Error al cargar los libros.'); // En caso de error, muestra un mensaje
      } finally {
        setIsLoading(false); // Termina la carga
      }
    };

    fetchLibros(); // Llama a la función para obtener los libros cuando se monta el componente
  }, [termino]);

  const handleAction = (action, id_libro) => {
    navigate(`/${action}/${id_libro}`); // Redirige a la acción correspondiente (reservar o préstamo)
  };

  if (isLoading) {
    return <div className="container mt-4">Cargando resultados...</div>; // Muestra un mensaje de carga
  }

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Resultados de Búsqueda {termino ? `para "${termino}"` : ''}</h1>
      {error && <div className="alert alert-danger" role="alert">{error}</div>}

      {libros.length > 0 ? (
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {libros.map((libro) => (
            <div key={libro.id_libro} className="col">
              <div className="card h-100">
                {libro.imagen && (
                  <img src={libro.imagen} alt={libro.titulo} className="card-img-top" />
                )}
                <div className="card-body">
                  <h5 className="card-title">{libro.titulo}</h5>
                  <p className="card-text">
                    <strong>Autor:</strong> {libro.autor}<br />
                    <strong>Categoría:</strong> {libro.categoria}
                  </p>
                  <div className="btn-group" role="group">
                    <button 
                      type="button" 
                      className="btn btn-primary" 
                      onClick={() => handleAction('reservar', libro.id_libro)}
                    >
                      Reservar
                    </button>
                    <button 
                      type="button" 
                      className="btn btn-primary" 
                      onClick={() => handleAction('prestamo', libro.id_libro)}
                    >
                      Préstamo
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-muted">
          {termino ? `No se encontraron libros para "${termino}"` : 'Realiza una búsqueda para ver resultados.'}
        </p>
      )}
    </div>
  );
};

export default CompResultadosBusqueda;
