import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true); // Activa el loading

    try {
      const response = await axios.post('http://localhost:8000/usuarios/login', { 
        email,
        contrasena 
      });

      // Guardar el token y el correo en localStorage
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('userEmail', email); // Guarda el correo del usuario

      // Verificar si el correo es el del administrador
      const isAdmin = email === 'admin@gmail.com';
      localStorage.setItem('isAdmin', isAdmin); // Guardar si es admin o no

      // Redirigir a la página de inicio
      navigate('/inicio');
    } catch (err) {
      setError(err.response?.data?.message || 'Credenciales incorrectas.');
    } finally {
      setIsLoading(false); // Desactiva el loading
    }
  };

  return (
    <div>
      <h2>Iniciar sesión</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Contraseña:</label>
          <input
            type="password"
            value={contrasena}
            onChange={(e) => setContrasena(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Cargando...' : 'Iniciar sesión'}
        </button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default Login;
