import React, { useState } from 'react';
import axios from 'axios';

const Restablecer = () => {
  
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:8000/usuarios/restablecer', { email });
      setMensaje(response.data.message);
    } catch (err) {
      setError(err.response?.data?.message || 'Error al enviar el correo');
    } finally {
      setIsLoading(false);
    }
  };


  return (
    <div className="restablecer-wrapper">
      <div className="restablecer-container">
        <h2>Restablecer Contraseña</h2>
        <form onSubmit={handleSubmit} className="restablecer-form">
          <div className="form-group">
            <label htmlFor="email">Correo Electrónico:</label>
            <input
              type="email"
              id="email"
              placeholder="Ingrese su correo"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-control"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary" disabled={isLoading}>
            {isLoading ? 'Enviando...' : 'Enviar Correo'}
          </button>
        </form>
        {mensaje && <p className="message" style={{ color: 'green' }}>{mensaje}</p>}
        {error && <p className="message" style={{ color: 'red' }}>{error}</p>}
      </div>
    </div>
  );
};

export default Restablecer;
