import React, { useState, useEffect, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import logo from './assets/logo.jpeg';
import perfilIcon from './assets/perfil.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css';
import MenuLateral from './MenuLateral.js';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const isAdmin = localStorage.getItem('isAdmin') === 'true';
  const [nombreUsuario, setNombreUsuario] = useState('');
  const [menuLateralAbierto, setMenuLateralAbierto] = useState(false);
  const [isNavCollapsed, setIsNavCollapsed] = useState(true);

  const menuLateralRef = useRef(null);

  const toggleMenuLateral = () => {
    setMenuLateralAbierto(!menuLateralAbierto);
  };

  const handleNavCollapse = () => setIsNavCollapsed(!isNavCollapsed);

  useEffect(() => {
    const nombre = localStorage.getItem('nombre');
    setNombreUsuario(nombre || 'Invitado');
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuLateralRef.current && !menuLateralRef.current.contains(event.target)) {
        setMenuLateralAbierto(false);
      }
    };

    document.addEventListener("click", handleClickOutside);

    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    setNombreUsuario('Invitado');
    navigate('/login');
  };

  const handleProfileClick = () => {
    navigate('/configuracion-perfil');
  };

  const isActive = (path) => location.pathname === path;

  // Cerrar sesión al cerrar la pestaña/navegador
  useEffect(() => {
    const handleBeforeUnload = () => {
      localStorage.clear(); // Limpia el localStorage
    };

    window.addEventListener("beforeunload", handleBeforeUnload);

    return () => {
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, []);

  return (
    <div>
      <nav className="navbar navbar-expand-lg custom-navbar">
        <div className="container-fluid">
          <div className="navbar-brand d-flex align-items-center">
            <img src={logo} alt="Logo" className="profile-logo me-2" />
            <span className="navbar-brand-title">Mario Hugo</span>
          </div>

          <button
            className="navbar-toggler"
            type="button"
            onClick={handleNavCollapse}
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className={`collapse navbar-collapse ${isNavCollapsed ? '' : 'show'}`}>
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <button className={`nav-link btn ${isActive('/') ? 'active' : ''}`} onClick={() => navigate('/')}>
                  Inicio
                </button>
              </li>
              {isAdmin && (
                <li className="nav-item">
                  <button className={`nav-link btn ${isActive('/admin') ? 'active' : ''}`} onClick={() => navigate('/admin')}>
                    Admin
                  </button>
                </li>
              )}
              <li className="nav-item">
                <button className="nav-link btn" onClick={toggleMenuLateral}>
                  Menú Lateral
                </button>
              </li>
              <li className="nav-item dropdown">
                <button className="nav-link btn dropdown-toggle" id="navbarDropdown" data-bs-toggle="dropdown" aria-expanded="false">
                  <img src={perfilIcon} alt="Perfil" className="profile-icon" />
                  {nombreUsuario}
                </button>
                <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="navbarDropdown">
                  <li>
                    <button className="dropdown-item" onClick={handleProfileClick}>
                      Configuración de Perfil
                    </button>
                  </li>
                  <li>
                    <hr className="dropdown-divider" />
                  </li>
                  <li>
                    <button className="dropdown-item" onClick={handleLogout}>
                      Cerrar Sesión
                    </button>
                  </li>
                </ul>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {menuLateralAbierto && (
        <div ref={menuLateralRef} className="menu-lateral">
          <MenuLateral onClose={toggleMenuLateral} />
        </div>
      )}
    </div>
  );
};

export default Navbar;
