import React, { useState } from 'react';
import './Restablecer.css'
import axios from 'axios';

const Restablecer = () => {
  const [email, setEmail] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:8000/usuarios/restablecer', { email });
      setMensaje(response.data.message);
    } catch (err) {
      setError(err.response?.data?.message || 'Error al enviar el correo');
    } finally {
      setIsLoading(false);
    }
  };
  

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6 col-lg-4">
          <div className="card shadow-lg rounded p-4">
            <h2 className="text-center text-primary mb-4">Restablecer Contraseña</h2>
            
            {/* Texto informativo */}
            <p className="text-center mb-4 text-muted">
              Para restablecer tu contraseña, ingresa tu correo electrónico. Si podemos encontrarlo en la base de datos, 
              te enviaremos un email con instrucciones para poder acceder de nuevo.
            </p>
  
            <form onSubmit={handleSubmit}>
              <div className="form-group mb-3">
                <label htmlFor="email" className="form-label">Correo Electrónico:</label>
                <input
                  type="email"
                  id="email"
                  className="form-control"
                  placeholder="Ingrese su correo"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <button
                type="submit"
                className="btn btn-primary w-100"
                disabled={isLoading}
              >
                {isLoading ? 'Enviando...' : 'Enviar Correo'}
              </button>
            </form>
            
            {mensaje && <p className="message mt-3 text-success">{mensaje}</p>}
            {error && <p className="message mt-3 text-danger">{error}</p>}
          </div>
        </div>
      </div>
    </div>
  );
  
};

export default Restablecer;
