import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import logo from './assets/logo.jpeg';
import perfilIcon from './assets/perfil.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css';

const Navbar = () => {
  const [nombreUsuario, setNombreUsuario] = useState('');
  const navigate = useNavigate();

  // Actualizar el nombre del usuario desde localStorage
  useEffect(() => {
    const nombre = localStorage.getItem('nombre');
    setNombreUsuario(nombre || 'Invitado');
  }, []);

  const handleLogout = () => {
    localStorage.clear(); // Limpiar todos los datos en localStorage
    setNombreUsuario('Invitado'); // Resetear el nombre de usuario
    navigate('/login'); // Redirigir al login
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to="/inicio">Mi Aplicación</Link>
      </div>
      <div className="navbar-links">
        <span>Bienvenido, {nombreUsuario}</span>
        {nombreUsuario !== 'Invitado' ? (
          <button onClick={handleLogout} className="btn-logout">
            Cerrar Sesión
          </button>
        ) : (
          <Link to="/login" className="btn-login">
            Iniciar Sesión
          </Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
