import axios from 'axios'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import './ShowUsuarios.js'

const URI = 'http://localhost:8000/usuarios'

const CompShowUsuarios = () => {
    const [usuarios, setUsuarios] = useState([])

    useEffect(() => {
        getUsuarios()
    }, [])

    const getUsuarios = async () => {
        try {
            const res = await axios.get(URI, {
                headers: {
                    "Authorization": `Bearer ${localStorage.getItem("token")}`
                }
            });
            setUsuarios(res.data);
        } catch (error) {
            console.error("Error al obtener los usuarios:", error);
            if (error.response) {
                // Esto ayudará a detectar errores del servidor como 403
                console.log("Código de estado:", error.response.status);
                console.log("Mensaje de error:", error.response.data);
                if (error.response.status === 403) {
                    alert("No tienes permiso para acceder a esta página.");
                    // Puedes redirigir al usuario a la página de inicio de sesión si lo deseas
                    // window.location.href = '/login';
                }
            } else {
                console.error("Error de solicitud:", error.message);
            }
        }
    };
   

    const deleteUsuario = async (id_usuario) => {
        const token = localStorage.getItem("token");
        try {
            await axios.delete(`${URI}/${id_usuario}`, {
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            });
            getUsuarios();
        } catch (error) {
            console.error("Error al eliminar el usuario:", error);
            if (error.response) {
                console.log("Código de estado:", error.response.status);
                console.log("Mensaje de error:", error.response.data);
            }
        }
    }

    return (
        <div className="vista-usuarios-container">
            <div className="row">
                <div className="col">
                    <Link 
                        to="/create-usuario" 
                        className="btn-crear-usuario mb-3"
                    >
                        Crear Usuario
                    </Link>
    
                    <div className="table-responsive">
                        <table className="table-usuarios">
                            <thead className="text-center">
                                <tr>
                                    <th>Nombre</th>
                                    <th>Email</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {usuarios.map((usuario) => (
                                    <tr key={usuario.id_usuario}>
                                        <td>{usuario.nombre}</td>
                                        <td>{usuario.email}</td>
                                        <td className="text-center">
                                            <div className="btn-group" role="group">
                                                <Link 
                                                    to={`/edit-usuario/${usuario.id_usuario}`} 
                                                    className="btn-editar btn-sm"
                                                >
                                                    Editar
                                                </Link>
                                                <button 
                                                    onClick={() => deleteUsuario(usuario.id_usuario)} 
                                                    className="btn-eliminar btn-sm"
                                                >
                                                    Eliminar
                                                </button>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
    
}

export default CompShowUsuarios
