import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Historial.css";

const Historial = ({ id_usuario }) => {
  const [historial, setHistorial] = useState(null);
  const [error, setError] = useState(null);

  // Función para cambiar el estado de una reserva
  const cambiarEstadoReserva = async (id_reserva, nuevoEstado) => {
    try {
      const response = await axios.put(
        `http://localhost:8000/libros/reservas/${id_reserva}`,
        { estado: nuevoEstado, id_usuario },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      // Actualizar el estado local después de cambiar el estado de la reserva
      setHistorial((prevHistorial) => {
        const reservasActualizadas = prevHistorial.reservas.map((reserva) =>
          reserva.id_reserva === id_reserva
            ? { ...reserva, estado: nuevoEstado }
            : reserva
        );
        return { ...prevHistorial, reservas: reservasActualizadas };
      });

      alert("Estado de la reserva actualizado con éxito.");
    } catch (error) {
      setError(error.response ? error.response.data.message : error.message);
    }
  };

  // Función para devolver el libro
  const devolverLibro = async (id_prestamo) => {
    try {
      const response = await axios.put(
        `http://localhost:8000/libros/prestamos/${id_prestamo}`,
        { devuelto: true },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      // Actualizar el estado local después de marcar el libro como devuelto
      setHistorial((prevHistorial) => {
        const prestamosActualizados = prevHistorial.prestamos.map((prestamo) =>
          prestamo.id_prestamo === id_prestamo
            ? { ...prestamo, devuelto: true, fecha_devolucion: new Date().toLocaleDateString() }
            : prestamo
        );
        return { ...prevHistorial, prestamos: prestamosActualizados };
      });

      alert("Libro marcado como devuelto con éxito.");
    } catch (error) {
      setError(error.response ? error.response.data.message : error.message);
    }
  };

  useEffect(() => {
    const fetchHistorial = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8000/libros/historial/${id_usuario}`,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        setHistorial(response.data);
      } catch (error) {
        setError(error.response ? error.response.data.message : error.message);
      }
    };

    fetchHistorial();
  }, [id_usuario]);

  if (error) {
    return <p className="historial-error">{error}</p>;
  }

  if (!historial) {
    return <p className="historial-loading">Cargando historial...</p>;
  }

  return (
    <div
      style={{
        backgroundColor: "#fef9f4",
        padding: "20px",
        maxWidth: "800px",
        margin: "20px auto",
        borderRadius: "10px",
        boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
      }}
    >
      <h2 style={{ textAlign: "center", color: "#8b5d33", fontFamily: "Poppins, sans-serif" }}>HISTORIAL</h2>

      {historial.reservas.length > 0 ? (
        <>
          <h3 style={{ color: "#8b5d33", fontFamily: "Poppins, sans-serif", marginBottom: "10px" }}>Reservas</h3>
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              fontFamily: "Poppins, sans-serif",
              backgroundColor: "#ffffff",
              borderRadius: "8px",
              overflow: "hidden",
            }}
          >
            <thead>
              <tr>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Libro</th>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Autor</th>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Fecha Reserva</th>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Estado</th>
              </tr>
            </thead>
            <tbody>
              {historial.reservas.map((reserva, index) => (
                <tr key={index} style={{ borderBottom: "1px solid #e0d5c3" }}>
                  <td style={{ padding: "10px" }}>{reserva.libro.titulo}</td>
                  <td style={{ padding: "10px" }}>{reserva.libro.autor}</td>
                  <td style={{ padding: "10px" }}>{reserva.fecha_reserva}</td>
                  <td style={{ padding: "10px" }}>
                    <select
                      style={{
                        fontFamily: "Poppins, sans-serif",
                        padding: "5px",
                        borderRadius: "4px",
                      }}
                      value={reserva.estado}
                      onChange={(e) => cambiarEstadoReserva(reserva.id_reserva, e.target.value)}
                    >
                      <option value="activo">Activo</option>
                      <option value="completo">Completo</option>
                      <option value="cancelado">Cancelado</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p style={{ textAlign: "center" }}>No tienes reservas registradas.</p>
      )}

      {historial.prestamos && historial.prestamos.length > 0 ? (
        <>
          <h3 style={{ color: "#8b5d33", fontFamily: "Poppins, sans-serif", marginBottom: "10px" }}>Préstamos</h3>
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              fontFamily: "Poppins, sans-serif",
              backgroundColor: "#ffffff",
              borderRadius: "8px",
              overflow: "hidden",
            }}
          >
            <thead>
              <tr>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Libro</th>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Autor</th>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Fecha Préstamo</th>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Estado de Devolución</th>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Fecha Devolución</th>
                <th style={{ backgroundColor: "#D8B38F", color: "white", padding: "10px", textAlign: "left" }}>Acción</th>
              </tr>
            </thead>
            <tbody>
              {historial.prestamos.map((prestamo, index) => (
                <tr key={index} style={{ borderBottom: "1px solid #e0d5c3" }}>
                  <td style={{ padding: "10px" }}>{prestamo.libro.titulo}</td>
                  <td style={{ padding: "10px" }}>{prestamo.libro.autor}</td>
                  <td style={{ padding: "10px" }}>{prestamo.fecha_prestamo}</td>
                  <td style={{ padding: "10px", color: prestamo.devuelto ? "#4CAF50" : "#f44336" }}>
                    {prestamo.devuelto ? "Devuelto" : "Pendiente"}
                  </td>
                  <td style={{ padding: "10px" }}>{prestamo.fecha_devolucion || "Pendiente"}</td>
                  <td>
                    {!prestamo.devuelto && (
                      <button
                        style={{
                          fontFamily: "Poppins, sans-serif",
                          padding: "5px 10px",
                          backgroundColor: "#4CAF50",
                          color: "white",
                          border: "none",
                          borderRadius: "4px",
                          cursor: "pointer",
                        }}
                        onClick={() => devolverLibro(prestamo.id_prestamo)}
                      >
                        Devolver
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p style={{ textAlign: "center" }}>No tienes préstamos registrados.</p>
      )}
    </div>
  );
};

export default Historial;
