import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CompReservasActivas = () => {
  const [reservas, setReservas] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReservasActivas = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:8000/reservas/activas', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setReservas(response.data.reservas);
      } catch (error) {
        console.error('Error al obtener reservas activas:', error);
        alert('Error al obtener reservas activas');
      } finally {
        setLoading(false);
      }
    };

    fetchReservasActivas();
  }, []);

  if (loading) {
    return <p>Cargando reservas activas...</p>;
  }

  if (reservas.length === 0) {
    return <p>No tienes reservas activas.</p>;
  }

  return (
    <div>
      <h2>Reservas Activas</h2>
      <ul>
        {reservas.map((reserva) => (
          <li key={reserva.id}>
            <p><strong>Libro ID:</strong> {reserva.id_libro}</p>
            <p><strong>Fecha de Reserva:</strong> {new Date(reserva.fecha_reserva).toLocaleDateString()}</p>
            <p><strong>Estado:</strong> {reserva.estado}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CompReservasActivas;
