import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import logo from './assets/logo.jpeg';
import perfilIcon from './assets/perfil.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const isAdmin = localStorage.getItem('isAdmin') === 'true';
  const [nombreUsuario, setNombreUsuario] = useState('');
  const [showSidebar, setShowSidebar] = useState(false);

  useEffect(() => {
    const nombre = localStorage.getItem('nombre');
    setNombreUsuario(nombre || 'Usuario');
  }, []);

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  const handleLogout = () => {
    // Limpia el localStorage y redirige al login
    localStorage.clear();
    navigate('/login');
  };

  const handleProfileClick = () => {
    navigate('/configuracion-perfil');
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg custom-navbar navbar-dark bg-dark">
        <div className="container-fluid">
          {/* Logo */}
          <div className="navbar-brand d-flex align-items-center">
            <img src={logo} alt="Logo" className="profile-logo me-2" />
            <span className="navbar-brand-title">Mario Hugo</span>
          </div>

          {/* Botón para toggler */}
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          {/* Menú */}
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active" href="/inicio">Inicio</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/catalogo">Catálogo</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/historial">Historial</a>
              </li>
              {isAdmin && (
                <li className="nav-item">
                  <a className="nav-link" href="/libros">Libros</a>
                </li>
              )}
            </ul>

            {/* Perfil */}
            <div
              className="profile d-flex align-items-center"
              onClick={toggleSidebar}
              style={{ cursor: 'pointer' }}
            >
              <span className="profile-text me-2">{nombreUsuario}</span>
              <img src={perfilIcon} alt="Icono de Perfil" className="profile-icon" />
            </div>
          </div>
        </div>
      </nav>

      {/* Sidebar */}
      {showSidebar && (
        <div className="sidebar">
          <div className="sidebar-content">
            <h4>Hola, {nombreUsuario}</h4>
            <ul>
              <li onClick={handleProfileClick}>Gestionar Perfil</li>
              <li onClick={handleLogout}>Cerrar Sesión</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default Navbar;
