import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const RestablecerContrasena = () => {
  const { token } = useParams(); // Obtener el token de la URL
  const navigate = useNavigate();
  const [mensaje, setMensaje] = useState('');
  const [error, setError] = useState('');
  const [isTokenValido, setIsTokenValido] = useState(false); // Para verificar si el token es válido
  const [nuevaContrasena, setNuevaContrasena] = useState('');
  const [confirmarContrasena, setConfirmarContrasena] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    // Verificar el token al cargar la página
    const verificarToken = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/restablecer/${token}`);
        setMensaje(response.data.message);
        setIsTokenValido(true);
      } catch (err) {
        setError(err.response?.data?.message || 'El enlace es inválido o ha expirado');
        setIsTokenValido(false);
      }
    };

    verificarToken();
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    if (nuevaContrasena !== confirmarContrasena) {
      setError('Las contraseñas no coinciden');
      setIsLoading(false);
      return;
    }

    try {
      const response = await axios.post(
        `http://localhost:8000/usuarios/actualizar/${token}`,
        { nuevaContrasena }
      );
      setMensaje(response.data.message);
      navigate('/login'); // Redirigir al login después de restablecer la contraseña
    } catch (err) {
      setError(err.response?.data?.message || 'Error al actualizar la contraseña');
    } finally {
      setIsLoading(false);
    }
  };

  if (!isTokenValido) {
    return <div><p>El enlace de restablecimiento no es válido o ha expirado.</p></div>;
  }

  return (
    <div className="restablecer-container">
      <h2>Restablecer Contraseña</h2>
      <form onSubmit={handleSubmit} className="restablecer-form">
        <div className="form-group">
          <label htmlFor="nuevaContrasena">Nueva Contraseña:</label>
          <input
            type="password"
            id="nuevaContrasena"
            placeholder="Ingrese nueva contraseña"
            value={nuevaContrasena}
            onChange={(e) => setNuevaContrasena(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="confirmarContrasena">Confirmar Contraseña:</label>
          <input
            type="password"
            id="confirmarContrasena"
            placeholder="Confirme su contraseña"
            value={confirmarContrasena}
            onChange={(e) => setConfirmarContrasena(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary" disabled={isLoading}>
          {isLoading ? 'Restableciendo...' : 'Restablecer Contraseña'}
        </button>
      </form>
      {mensaje && <p className="message" style={{ color: 'green' }}>{mensaje}</p>}
      {error && <p className="message" style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default RestablecerContrasena;
