import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "./Autenticacion/auth.js";
import './Login.css';
import logo from './assets/logo.jpeg'; // Importación del logo

const Login = () => {
  const [credentials, setCredentials] = useState({ email: '', contrasena: '' });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Maneja cambios en los campos de entrada
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials((prev) => ({ ...prev, [name]: value }));
  };

  // Maneja el envío del formulario y la autenticación
  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
  
    try {
      const success = await login(credentials.email, credentials.contrasena);
      if (success) {
        // Guarda el token en el localStorage
        localStorage.setItem('token', success.token);
        setTimeout(() => navigate("/"), 1000); // Redirige después de iniciar sesión
      } else {
        setError("Credenciales incorrectas. Inténtalo de nuevo.");
      }
    } catch (error) {
      console.error("Error de inicio de sesión:", error);
      setError("Error de inicio de sesión. Por favor, verifica tus credenciales.");
    } finally {
      setIsLoading(false);
    }
  };

  // Maneja el clic en el botón "Registrarse"
  const handleRegisterClick = () => {
    navigate("/registro");
  };

  return (
    <div className="login-wrapper">
      <div className="login-container">
        <h2>Biblioteca Pública<br />Mario Hugo Marino Ortiz</h2>
        <img src={logo} alt="Logo" className="logo-image" /> {/* Logo debajo del título */}
        <form onSubmit={handleLogin} className="login-form">
          <div className="form-group">
            <input
              type="email"
              placeholder="Correo"
              name="email"
              value={credentials.email}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              placeholder="Contraseña"
              name="contrasena"
              value={credentials.contrasena}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary" disabled={isLoading}>
            {isLoading ? 'Cargando...' : 'Iniciar sesión'}
          </button>
          <button 
            type="button" 
            className="btn btn-secondary" 
            onClick={handleRegisterClick}
          >
            Registrarse
          </button>
        </form>
        {error && <p className="message">{error}</p>}
      </div>
    </div>
  );
};

export default Login;
