import React, { useEffect, useState } from "react";

const Historial = ({ id_usuario }) => {
    const [historial, setHistorial] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchHistorial = async () => {
            try {
                const response = await fetch(`http://localhost:8000/libros/historial/${id_usuario}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`, // Si usas JWT para autenticación
                    },
                });

                if (!response.ok) {
                    throw new Error("Error al obtener el historial");
                }

                const data = await response.json();
                setHistorial(data); // Actualiza el estado con los datos obtenidos
            } catch (error) {
                setError(error.message);
            }
        };

        fetchHistorial();
    }, [id_usuario]); // Ejecuta el efecto cuando userId cambie

    if (error) {
        return <p>{error}</p>;
    }

    if (!historial) {
        return <p>Cargando historial...</p>;
    }

    return (
        <div>
            <h1>Historial de Reservas y Préstamos</h1>
            <h2>Préstamos</h2>
            {historial.prestamos.length > 0 ? (
                historial.prestamos.map((prestamo) => (
                    <div key={prestamo.id_prestamo}>
                        <h3>{prestamo.libro.titulo}</h3>
                        <p>Autor: {prestamo.libro.autor}</p>
                        <p>Fecha de préstamo: {prestamo.fecha_prestamo}</p>
                        <p>Fecha de devolución: {prestamo.fecha_devolucion}</p>
                        <p>Devuelto: {prestamo.devuelto ? "Sí" : "No"}</p>
                    </div>
                ))
            ) : (
                <p>No tienes préstamos registrados.</p>
            )}

            <h2>Reservas</h2>
            {historial.reservas.length > 0 ? (
                historial.reservas.map((reserva) => (
                    <div key={reserva.id_reserva}>
                        <h3>{reserva.libro.titulo}</h3>
                        <p>Autor: {reserva.libro.autor}</p>
                        <p>Fecha de reserva: {reserva.fecha_reserva}</p>
                        <p>Estado: {reserva.estado}</p>
                    </div>
                ))
            ) : (
                <p>No tienes reservas registradas.</p>
            )}
        </div>
    );
};

export default Historial;
