import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './FormularioPrestamo.css';

const CompPrestamoForm = () => {
  const { id_libro } = useParams();
  const [formData, setFormData] = useState({
    titulo: '',
    id_libro: id_libro || '',
    id_usuario: '',
    nombres: '',
    ap_paterno: '',
    ap_materno: '',
    domicilio: '',
    fecha_prestamo: '',
    fecha_devolucion: '',
    estado: 'activo',
  });

  const [prestamoSuccess, setPrestamoSuccess] = useState(false); // Estado para éxito del préstamo
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }

    // Cargar el título del libro utilizando el ID del libro
    const fetchBookTitle = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/libros/${id_libro}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setFormData((prev) => ({
          ...prev,
          titulo: response.data.titulo || '', // Asignar el título al estado
        }));
      } catch (error) {
        console.error('Error al cargar el título del libro:', error);
      }
    };

    fetchBookTitle();
  }, [id_libro, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');

      const response = await axios.post('http://localhost:8000/libros/prestar', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      // Actualizar el estado para mostrar el mensaje de éxito
      setPrestamoSuccess(true);

      // Limpiar los campos del formulario, excepto los valores estáticos
      setFormData((prev) => ({
        ...prev,
        id_usuario: '',
        nombres: '',
        ap_paterno: '',
        ap_materno: '',
        domicilio: '',
        fecha_prestamo: '',
        fecha_devolucion: '',
      }));
    } catch (error) {
      console.error('Error al prestar el libro:', error);
      alert(error.response?.data.message || 'Error al prestar el libro');
    }
  };

  return (
    <div className="form-container">
      <h2>Formulario de Préstamo</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Título del Libro
          <input
            type="text"
            name="titulo"
            value={formData.titulo}
            readOnly
            className="read-only"
          />
        </label>
        <label>
          Nombre(s)
          <input
            type="text"
            name="nombres"
            value={formData.nombres}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Apellido Paterno
          <input
            type="text"
            name="ap_paterno"
            value={formData.ap_paterno}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Apellido Materno
          <input
            type="text"
            name="ap_materno"
            value={formData.ap_materno}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Domicilio
          <input
            type="text"
            name="domicilio"
            value={formData.domicilio}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Fecha de Préstamo
          <input
            type="date"
            name="fecha_prestamo"
            value={formData.fecha_prestamo}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Fecha de Devolución
          <input
            type="date"
            name="fecha_devolucion"
            value={formData.fecha_devolucion}
            onChange={handleInputChange}
            required
          />
        </label>
        <button type="submit">Pedir prestado</button>
      </form>

      {prestamoSuccess && (
        <div className="success-message">
          <p>¡El préstamo ha sido registrado exitosamente!</p>
        </div>
      )}
    </div>
  );
};

export default CompPrestamoForm;
