import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const URI = 'http://localhost:8000/usuarios'; // Cambiar la URI según el backend

const CompCreateUsuario = () => {
    const [nombre, setNombre] = useState('');
    const [email, setEmail] = useState('');
    const [idRol, setIdRol] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // Procedimiento para crear usuario
    const store = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem("token"); 
        
        // Validación de campos vacíos
        if (!nombre || !email || !idRol) {
            setError("Por favor, complete todos los campos.");
            return;
        }

        setError(''); // Limpiar mensaje de error
        try {
            await axios.post(URI, 
                {
                    nombre: nombre,
                    email: email,
                    id_rol: idRol
                },
                {
                    headers: {
                        "Authorization": `Bearer ${token}`
                    }
                }
            );
            navigate('/usuarios'); // Redirige a la página de usuarios
        } catch (error) {
            if (error.response) {
                console.error("Error de la solicitud:");
                console.error("Código de estado:", error.response.status);
                console.error("Datos de error:", error.response.data);
                console.error("Encabezados de respuesta:", error.response.headers);
                setError("Error al crear el usuario, intente nuevamente.");
            } else if (error.request) {
                console.error("La solicitud fue realizada, pero no se recibió respuesta");
                setError("No se recibió respuesta del servidor.");
            } else {
                console.error("Error en la configuración de la solicitud");
                setError("Error en la configuración de la solicitud.");
            }
        }
    };
    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Crear Usuario</h1>
            {error && <div className="alert alert-danger">{error}</div>} {/* Mostrar error si existe */}
            <form onSubmit={store}>
                <div className="row">
                    <div className="col-md-6">
                        <div className="mb-3">
                            <label className="form-label">Nombre</label>
                            <input 
                                value={nombre}
                                onChange={(e) => setNombre(e.target.value)}
                                type="text"
                                className="form-control"
                                placeholder="Ingrese el nombre"
                            />
                        </div>
                    </div>
                    <div className="col-md-6">
                        <div className="mb-3">
                            <label className="form-label">Email</label>
                            <input 
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                type="email"
                                className="form-control"
                                placeholder="Ingrese el correo"
                            />
                        </div>
                    </div>
                </div>

                <div className="row">
                    <div className="col-md-6">
                        <div className="mb-3">
                            <label className="form-label">Rol</label>
                            <select 
                                value={idRol}
                                onChange={(e) => setIdRol(e.target.value)}
                                className="form-control"
                            >
                                <option value="">Seleccione un rol</option>
                                <option value="1">Administrador</option>
                                <option value="2">Usuario</option>
                                {/* Agregar más roles según tu sistema */}
                            </select>
                        </div>
                    </div>
                </div>

                <button type="submit" className="btn btn-primary">Guardar</button>
            </form>
        </div>
            );
        };
        
        export default CompCreateUsuario;
