import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const CompReservaForm = () => {
  const { id_libro } = useParams(); // Obtener el id del libro desde la URL
  const [formData, setFormData] = useState({
    id_libro: id_libro || '',  // Agregar id_libro al estado
    nombres: '',
    ap_paterno: '',
    ap_materno: '',
    fecha_reserva: '',
    estado: 'activo', // Si el estado no se pasa, será 'activo' por defecto
  });

  const [isReservaExitosa, setIsReservaExitosa] = useState(false);
  const [pdfUrl, setPdfUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');  // Asegúrate de que el token esté disponible

      // Enviar la reserva
      const response = await axios.post('http://localhost:8000/libros/reservar', formData, {
        headers: {
          Authorization: `Bearer ${token}`,  // Enviar el token en los headers
        },
      });

      alert(response.data.message);  // Mostrar mensaje de éxito
      setIsReservaExitosa(true);  // Indicar que la reserva fue exitosa

      // Solicitar el PDF
      const pdfResponse = await axios.post('http://localhost:8000/generar-pdf-reserva', formData, {
        headers: {
          Authorization: `Bearer ${token}`,  // Enviar el token para la autorización
        },
        responseType: 'blob',  // Especificar que esperamos un blob (archivo binario)
      });

      // Verificar si la respuesta es válida y crear el enlace de descarga
      if (pdfResponse.status === 200) {
        const url = window.URL.createObjectURL(pdfResponse.data);  // Crear un URL para el blob
        setPdfUrl(url);  // Guardar la URL del archivo PDF
      } else {
        alert('No se pudo generar el PDF');
      }
    } catch (error) {
      console.error('Error al reservar el libro:', error);
      alert(error.response?.data.message || 'Error al reservar el libro');  // Mostrar mensaje de error
    }
  };

  return (
    <div>
      <h2>Reserva de Libro</h2>
      <form onSubmit={handleSubmit}>
        {/* Aquí van los campos de tu formulario */}
        <input
          type="text"
          name="id_usuario"
          value={formData.id_usuario}
          onChange={(e) => setFormData({ ...formData, id_usuario: e.target.value })}
        />
        <input
          type="text"
          name="id_libro"
          value={formData.id_libro}
          onChange={(e) => setFormData({ ...formData, id_libro: e.target.value })}
        />
        <input
          type="date"
          name="fecha_reserva"
          value={formData.fecha_reserva}
          onChange={(e) => setFormData({ ...formData, fecha_reserva: e.target.value })}
        />
        <input
          type="date"
          name="fecha_devolucion"
          value={formData.fecha_devolucion}
          onChange={(e) => setFormData({ ...formData, fecha_devolucion: e.target.value })}
        />
        <button type="submit">Reservar</button>
      </form>

      {isReservaExitosa && (
        <div>
          <p>¡Reserva exitosa!</p>
          {pdfUrl && (
            <a href={pdfUrl} download={`reserva_${formData.id_usuario}.pdf`}>
              <button>Descargar ficha de reserva en PDF</button>
            </a>
          )}
        </div>
      )}
    </div>
  );
};

export default CompReservaForm;
