import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './FormularioPrestamo.css';
import { useLocation } from 'react-router-dom';

const CompPrestamoForm = () => {
  const { id_libro } = useParams();
  const [formData, setFormData] = useState({
    titulo: '', // Título estará vacío inicialmente
    id_libro: id_libro || '',
    id_usuario: '',
    nombres: '',
    ap_paterno: '',
    ap_materno: '',
    domicilio: '',  
    fecha_prestamo: '',
    fecha_devolucion: '',
    estado: 'activo',
  });

  const [prestamoSuccess, setPrestamoSuccess] = useState(false); // Estado para éxito del préstamo
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }

    // Cargar el título del libro utilizando el ID del libro
    const fetchBookTitle = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/libros/${id_libro}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setFormData((prev) => ({
          ...prev,
          titulo: response.data.titulo || '', // Asignar el título al estado
        }));
      } catch (error) {
        console.error('Error al cargar el título del libro:', error);
      }
    };

    fetchBookTitle();
  }, [id_libro, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');

      const response = await axios.post('http://localhost:8000/libros/prestar', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert(response.data.message);
      setPrestamoSuccess(true);
    } catch (error) {
      console.error('Error al prestar el libro:', error);
      alert(error.response?.data.message || 'Error al prestar el libro');
    }
  };

  const handleGeneratePDF = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Token de autorización no encontrado. Por favor, inicie sesión nuevamente.');
        return;
      }

      const response = await axios.post(
        'http://localhost:8000/libros/generar-pdf-prestamo',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
          responseType: 'blob', // Para recibir el archivo PDF en formato blob
        }
      );

      if (response.status === 200) {
        const pdfBlob = new Blob([response.data], { type: 'application/pdf' });
        const pdfUrl = window.URL.createObjectURL(pdfBlob);
        const link = document.createElement('a');
        link.href = pdfUrl;
        link.download = `prestamo_${formData.id_usuario}.pdf`;
        link.click();
        window.URL.revokeObjectURL(pdfUrl);
      } else {
        console.error('Error al generar el PDF: Respuesta inesperada', response);
        alert('Error al generar el PDF. Intente nuevamente.');
      }
    } catch (error) {
      console.error('Error al generar el PDF:', error.response?.data || error);
      alert(error.response?.data?.message || 'Error al generar el PDF. Por favor, verifique la consola para más detalles.');
    }
  };

  return (
    <div className="historial-container">
      <h2 className="historial-title">HISTORIAL</h2>

      {historial.reservas.length > 0 ? (
        <>
          <h3 className="historial-subtitle">Reservas</h3>
          <table className="historial-table">
            <thead>
              <tr>
                <th>Libro</th>
                <th>Autor</th>
                <th>Fecha Reserva</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {historial.reservas.map((reserva, index) => (
                <tr key={index}>
                  <td>{reserva.libro.titulo}</td>
                  <td>{reserva.libro.autor}</td>
                  <td>{reserva.fecha_reserva}</td>
                  <td>
                    <select
                      className="historial-select"
                      value={reserva.estado}
                      onChange={(e) => cambiarEstadoReserva(reserva.id_reserva, e.target.value)}
                    >
                      <option value="activo">Activo</option>
                      <option value="completo">Completo</option>
                      <option value="cancelado">Cancelado</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p className="historial-empty">No tienes reservas registradas.</p>
      )}

      {historial.prestamos && historial.prestamos.length > 0 ? (
        <>
          <h3 className="historial-subtitle">Préstamos</h3>
          <table className="historial-table">
            <thead>
              <tr>
                <th>Libro</th>
                <th>Autor</th>
                <th>Fecha Préstamo</th>
                <th>Estado de Devolución</th>
                <th>Fecha Devolución</th>
                <th>Acción</th>
              </tr>
            </thead>
            <tbody>
              {historial.prestamos.map((prestamo, index) => (
                <tr key={index}>
                  <td>{prestamo.libro.titulo}</td>
                  <td>{prestamo.libro.autor}</td>
                  <td>{prestamo.fecha_prestamo}</td>
                  <td>{prestamo.devuelto ? "Devuelto" : "Pendiente"}</td>
                  <td>{prestamo.fecha_devolucion}</td>
                  <td>
                    {!prestamo.devuelto && (
                      <button
                        className="historial-devolver-btn"
                        onClick={() => devolverLibro(prestamo.id_prestamo)}
                      >
                        Devolver
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p className="historial-empty">No tienes préstamos registrados.</p>
      )}
    </div>
  );
};


export default CompPrestamoForm;
