import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import logo from './assets/logo.jpeg';
import perfilIcon from './assets/perfil.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css';
import MenuLateral from './MenuLateral.js'; 

const Navbar = () => {
  const navigate = useNavigate(); 
  const isAdmin = localStorage.getItem('isAdmin') === 'true';
  const [nombreUsuario, setNombreUsuario] = useState('');
  const [menuLateralAbierto, setMenuLateralAbierto] = useState(false); // Estado para el menú lateral

  const toggleMenuLateral = () => {
    setMenuLateralAbierto(!menuLateralAbierto); 
  };

  useEffect(() => {
    const nombre = localStorage.getItem('nombre');
    setNombreUsuario(nombre || 'Invitado'); // Si no hay nombre, muestra como inivitado
  }, []);
  

  const handleLogout = () => {
    localStorage.clear(); // Limpiar datos del localStorage
    setNombreUsuario('Invitado'); // Restablecer el nombre del usuario
    navigate('/login'); // Redirigir al login
  };
  

  const handleProfileClick = () => {
    navigate('/configuracion-perfil');
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg custom-navbar navbar-dark bg-dark">
        <div className="container-fluid">
          {/* Logo */}
          <div className="navbar-brand d-flex align-items-center">
            <img src={logo} alt="Logo" className="profile-logo me-2" />
            <span className="navbar-brand-title">Mario Hugo</span>
          </div>

          {/* Botón */}
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          {/* Menú */}
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active" href="/inicio">Inicio</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/catalogo">Catálogo</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/historial">Historial</a>
              </li>
              {isAdmin && (
                <li className="nav-item">
                  <a className="nav-link" href="/libros">Libros</a>
                </li>
                
              )}
            </ul>

            {/* Perfil */}
            <div
              className="profile d-flex align-items-center"
              style={{ cursor: 'pointer' }}
              onClick={toggleMenuLateral}
            >
              <span className="profile-text me-2">{nombreUsuario}</span>
              <img src={perfilIcon} alt="Icono de Perfil" className="profile-icon" />
            </div>
          </div>
        </div>
      </nav>

      {/* Menú lateral */}
      <MenuLateral
        isOpen={menuLateralAbierto}
        toggleMenu={toggleMenuLateral}
        handleLogout={handleLogout}
        handleProfileClick={handleProfileClick}
      />
    </div>
  );
};

export default Navbar;
