import React, { useState, useEffect } from "react";
import axios from 'axios';

const Gestion = () => {
  const [reservas, setReservas] = useState([]);  // Inicializado como arreglo vacío
  const [loading, setLoading] = useState(true);  // Para mostrar un indicador de carga
  const [error, setError] = useState(null);  // Para manejar errores de la solicitud

  // Función para obtener todas las reservas
  const obtenerReservas = async () => {
    try {
      const response = await axios.get('http://localhost:8000/libros/reservas', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setReservas(response.data.reservas || []);  // Asegurarse de que sea un arreglo
      setLoading(false);
    } catch (err) {
      setError('Error al cargar las reservas');
      setLoading(false);
    }
  };

  // Función para actualizar el estado de la reserva
  const handleActualizarEstado = async (id_reserva, nuevoEstado) => {
    try {
      const response = await axios.put(
        `http://localhost:8000/libros/reservas/${id_reserva}/estado`,
        { estado: nuevoEstado },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      alert(response.data.message);
      obtenerReservas();  // Vuelve a cargar las reservas después de actualizar el estado
    } catch (error) {
      alert('Error al actualizar el estado de la reserva');
    }
  };

  // Cargar las reservas cuando el componente se monte
  useEffect(() => {
    obtenerReservas();
  }, []);

  if (loading) {
    return <div>Cargando reservas...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Gestionar Reservas</h2>
      <table>
        <thead>
          <tr>
            <th>ID Reserva</th>
            <th>Libro</th>
            <th>Usuario</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(reservas) && reservas.map((reserva) => (
            <tr key={reserva.id_reserva}>
              <td>{reserva.id_reserva}</td>
              <td>{reserva.libro ? reserva.libro.titulo : 'N/A'}</td>  {/* Verificar existencia de libro */}
              <td>{reserva.usuario ? reserva.usuario.nombre : 'N/A'}</td>  {/* Verificar existencia de usuario */}
              <td>{reserva.estado}</td>
              <td>
                <button onClick={() => handleActualizarEstado(reserva.id_reserva, 'Aprobada')}>
                  Aprobar
                </button>
                <button onClick={() => handleActualizarEstado(reserva.id_reserva, 'Rechazada')}>
                  Rechazar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Gestion;
