'use client'

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const RestablecerContrasena = () => {
  const { token } = useParams();
  const navigate = useNavigate();
  const [isTokenValid, setIsTokenValid] = useState(false);
  const [nuevaContrasena, setNuevaContrasena] = useState('');
  const [confirmarContrasena, setConfirmarContrasena] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const verificarToken = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/usuarios/restablecer-contrasena/${token}`);
        if (response.data.isValid) {
          setIsTokenValid(true);
        }
      } catch (error) {
        setError('El token no es válido o ha expirado');
      }
    };

    verificarToken();
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMensaje('');

    if (nuevaContrasena !== confirmarContrasena) {
      setError('Las contraseñas no coinciden');
      return;
    }

    try {
      const response = await axios.put(
        `http://localhost:8000/usuarios/restablecer/${token}`,
        { nuevaContrasena }
      );
      setMensaje(response.data.message);
      setTimeout(() => navigate('/login'), 3000); // Redirige al login después de 3 segundos
    } catch (error) {
      setError(error.response?.data?.message || 'Error al actualizar la contraseña');
    }
  };

  if (!isTokenValid) {
    return <div className="text-center mt-10 text-red-600">{error}</div>;
  }

  return (
    <div className="restablecer-contrasena">
      <h2 className="titulo-c">Restablecer Contraseña</h2>
      
      {/* Texto informativo */}
      <p className="texto">
        Ingresa tu nueva contraseña y confírmala para poder restablecer tu acceso. Asegúrate de que ambas contraseñas coincidan.
      </p>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="nuevaContrasena" className="input-c">Nueva Contraseña</label>
          <input
            type="password"
            id="nuevaContrasena"
            value={nuevaContrasena}
            onChange={(e) => setNuevaContrasena(e.target.value)}
            className="input-d"
            required
          />
        </div>
  
        <div>
          <label htmlFor="confirmarContrasena" className="input-c">Confirmar Contraseña</label>
          <input
            type="password"
            id="confirmarContrasena"
            value={confirmarContrasena}
            onChange={(e) => setConfirmarContrasena(e.target.value)}
            className="input-d"
            required
          />
        </div>
  
        <button 
          type="submit" 
          className="btn-contraseña"
        >
          Restablecer Contraseña
        </button>
      </form>
  
      {/* Mensajes */}
      {mensaje && <p className="mt-4 text-sm text-green-600">{mensaje}</p>}
      {error && <p className="mt-4 text-sm text-red-600">{error}</p>}
    </div>
  );
  
};

export default RestablecerContrasena;
