import React, { useState, useEffect, useRef } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { handleLogin } from './auth.js';

import logo from './assets/logo.jpeg';
import perfilIcon from './assets/perfil.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css'; // Mantén la importación de tus estilos
import MenuLateral from './MenuLateral.js';
import { Link } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const isAdmin = localStorage.getItem('isAdmin') === 'true';
  const [nombreUsuario, setNombreUsuario] = useState('Invitado');
  const [menuLateralAbierto, setMenuLateralAbierto] = useState(false);
  const [isNavCollapsed, setIsNavCollapsed] = useState(true);

  const menuLateralRef = useRef(null);

  // Función para alternar la visibilidad del menú lateral
  const toggleMenuLateral = () => {
    setMenuLateralAbierto(!menuLateralAbierto);
  };

  // Función para manejar el colapso del nav
  const handleNavCollapse = () => setIsNavCollapsed(!isNavCollapsed);

  // Hook para establecer el nombre del usuario desde el localStorage
  useEffect(() => {
    const nombre = localStorage.getItem('nombre');
    if (nombre) {
      setNombreUsuario(nombre); // Actualiza el nombre cuando el componente se monta
    }
  }, []); // Este useEffect solo se ejecuta una vez cuando el componente se monta

  // Función para manejar el cierre de sesión
  const handleLogout = () => {
    localStorage.clear();
    setNombreUsuario('Invitado');
    navigate('/login');
  };

  // Función para manejar el clic en el perfil
  const handleProfileClick = () => {
    navigate('/configuracion-perfil');
  };

  // Función para manejar el login y actualizar el nombre de usuario
  const handleLogin = (nombre) => {
    localStorage.setItem('nombre', nombre);  // Guarda el nombre del usuario en localStorage
    setNombreUsuario(nombre);  // Actualiza el estado con el nombre del usuario
    navigate('/inicio');  // Redirige a la página principal
  };

  const esTokenExpirado = (token) => {
    if (!token) return true; // Si no hay token, está "expirado"
  
    try {
      const payload = JSON.parse(atob(token.split(".")[1])); // Decodificar payload del JWT
      const ahora = Math.floor(Date.now() / 1000); // Tiempo actual en segundos
      return payload.exp < ahora; // Comparar expiración con el tiempo actual
    } catch (e) {
      return true; // Si el token no es válido
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const payload = JSON.parse(atob(token.split(".")[1]));
      const tiempoRestante = payload.exp * 1000 - Date.now(); // Diferencia en milisegundos
      const timeoutId = setTimeout(() => {
        localStorage.clear();
        setNombreUsuario('Invitado');
        navigate('/login');
      }, tiempoRestante > 0 ? tiempoRestante : 0);
      return () => clearTimeout(timeoutId);
    }
  }, []);

  // Función para verificar si la ruta está activa
  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg custom-navbar">
        <div className="container-fluid">
          {/* Logo e Icono de Perfil */}
          <div className="d-flex align-items-center">
            <img src={logo} alt="Logo" className="profile-logo" />
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded={!isNavCollapsed ? true : false}
              aria-label="Toggle navigation"
              onClick={handleNavCollapse}
            >
              <span className="navbar-toggler-icon"></span>
            </button>
          </div>

          {/* Título central visible solo en pantallas grandes */}
          <div className="navbar-title">
            <h5><strong>MARIO HUGO MARINO ORTIZ</strong></h5>
          </div>

          {/* Menú de navegación */}
          <div className={`${isNavCollapsed ? 'collapse' : ''} navbar-collapse`} id="navbarNav">
            <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link 
                  className={`nav-link ${isActive('/inicio') ? 'active' : ''}`} 
                  to="/inicio"
                  style={{ color: '#ffffff' }}
                >
                  Inicio
                </Link>
              </li>
              <li className="nav-item">
                <Link 
                  className={`nav-link ${isActive('/catalogo') ? 'active' : ''}`} 
                  to="/catalogo"
                  style={{ color: '#ffffff' }}
                >
                  Catálogo
                </Link>
              </li>
              <li className="nav-item">
                <Link 
                  className={`nav-link ${isActive('/historial') ? 'active' : ''}`} 
                  to="/historial"
                  style={{ color: '#ffffff' }}
                >
                  Historial
                </Link>
              </li>
              {isAdmin && (
                <li className="nav-item">
                  <Link 
                    className={`nav-link ${isActive('/libros') ? 'active' : ''}`} 
                    to="/libros"
                    style={{ color: '#ffffff' }}
                  >
                    Libros
                  </Link>
                </li>
              )}
              {isAdmin && (
                <li className="nav-item">
                  <Link 
                    className={`nav-link ${isActive('/usuarios') ? 'active' : ''}`} 
                    to="/usuarios"
                    style={{ color: '#ffffff' }}
                  >
                    Usuarios
                  </Link>
                </li>
              )}
            </ul>
          </div>

          {/* Icono de perfil */}
          <div className="profile d-flex align-items-center" onClick={toggleMenuLateral} style={{ cursor: 'pointer' }}>
            <span className="profile-text me-2">{nombreUsuario}</span>
            <img src={perfilIcon} alt="Icono de Perfil" className="profile-icon" />
          </div>
        </div>
      </nav>

      <MenuLateral
        ref={menuLateralRef}
        isOpen={menuLateralAbierto}
        toggleMenu={toggleMenuLateral}
        handleLogout={handleLogout}
        handleProfileClick={handleProfileClick}
      />
    </div>
  );
};

export default Navbar;
