import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import logo from './assets/logo.jpeg'; 
import perfilIcon from './assets/perfil.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();

  // Estado para guardar el nombre del usuario
  const [nomusuario, setNomusuario] = useState('');

  // Obtener el rol de usuario desde localStorage
  const isAdmin = localStorage.getItem('isAdmin') === 'true';

  // useEffect para cargar el nombre al montar el componente
  useEffect(() => {
    const nombre = localStorage.getItem('token');
    if (nombre) {
      setNomusuario(nombre); // Actualiza el estado si el nombre está en localStorage
    }
  }, []); // Solo se ejecuta al cargar el componente

  const handleProfileClick = () => {
    navigate('/configuracion-perfil'); // Redirige a la vista de configuración del perfil
  };

  return (
    <nav className="navbar navbar-expand-lg custom-navbar navbar-dark bg-dark">
      <div className="container-fluid">
        {/* Logo y nombre */}
        <div className="navbar-brand d-flex align-items-center">
          <img src={logo} alt="Logo" className="profile-logo me-2" />
          <span className="navbar-brand-title" style={{ color: '#ffffff' }}>Mario Hugo</span>
        </div>

        {/* Botón para el menú colapsable */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Menú colapsable */}
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <a className="nav-link active" href="/inicio" style={{ color: '#ffffff' }}>Inicio</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="/catalogo" style={{ color: '#ffffff' }}>Catálogo</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="/historial" style={{ color: '#ffffff' }}>Historial</a>
            </li>
            {/* Mostrar la sección "Libros" solo si el usuario es administrador */}
            {isAdmin && (
              <li className="nav-item">
                <a className="nav-link" href="/libros" style={{ color: '#ffffff' }}>Libros</a>
              </li>
            )}
          </ul>

          {/* Perfil del usuario */}
          <div
            className="profile d-flex align-items-center"
            onClick={handleProfileClick}
            style={{ cursor: 'pointer' }}
          >
            <span className="profile-text me-2" style={{ color: '#ffffff' }}>
              {nomusuario || 'User'} {/* Si el nombre está vacío, se muestra 'User' */}
            </span>
            <img src={perfilIcon} alt="Icono de Perfil" className="profile-icon" />
          </div>
        </div>
      </div>

      {/* Título debajo de la barra de navegación */}
      <div className="navbar-title">
        <h5 style={{ color: '#ffffff' }}>MARIO HUGO MARINO ORTIZ</h5>
      </div>
    </nav>
  );
};

export default Navbar;
