import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ConfiguracionPerfil.css'; 

const ConfiguracionPerfil = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const obtenerPerfil = async (req, res) => {
    try {
      const id_usuario = req.params.id_usuario;
      const usuario = await UsuarioModel.findByPk(id_usuario);
      
      if (!usuario) {
        return res.status(404).json({ message: 'Usuario no encontrado' });
      }
      
      res.json({ usuario });
    } catch (error) {
      console.error(error);
      res.status(500).json({ message: 'Error al obtener el perfil' });
    }
  };
  
  useEffect(() => {
    // Llamar al backend para obtener el perfil actual
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/perfil/${localStorage.getItem('id_usuario')}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          }
        });        
        setNombre(response.data.usuario.nombre);
        setEmail(response.data.usuario.email);
      } catch (err) {
        setError('Error al cargar el perfil');
      }
    };
  
    fetchProfile();
  }, []);
  
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.put(`http://localhost:8000/perfil/${localStorage.getItem('id_usuario')}`, 
      { nombre, email }, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        }
      });
      setSuccessMessage(response.data.message);
    } catch (err) {
      setError('Error al actualizar el perfil: ' + err.message);
    }
  };
  

  return (
    <div className="configuracion-perfil-container">
      <h2>Configuración del Perfil</h2>
      
      <div className="perfil-card">
        <form className="perfil-formulario" onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Nombre</label>
            <input 
              type="text" 
              value={nombre} 
              onChange={(e) => setNombre(e.target.value)} 
              placeholder="Tu nombre" 
            />
          </div>
          <div className="form-group">
            <label>Correo Electrónico</label>
            <input 
              type="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              placeholder="tucorreo@ejemplo.com" 
            />
          </div>
          <button type="submit" className="btn-guardar">Guardar Cambios</button>
        </form>

        {/* Mostrar errores o mensajes de éxito */}
        {error && <p className="error-message">{error}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}
      </div>
    </div>
  );
};

export default ConfiguracionPerfil;
