'use client'

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const RestablecerContrasena = () => {
  const { token } = useParams();
  const navigate = useNavigate();
  const [isTokenValid, setIsTokenValid] = useState(false);
  const [nuevaContrasena, setNuevaContrasena] = useState('');
  const [confirmarContrasena, setConfirmarContrasena] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const verificarToken = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/usuarios/restablecer-contrasena/${token}`);
        if (response.data.isValid) {
          setIsTokenValid(true);
        }
      } catch (error) {
        setError('El token no es válido o ha expirado');
      }
    };

    verificarToken();
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMensaje('');

    if (nuevaContrasena !== confirmarContrasena) {
      setError('Las contraseñas no coinciden');
      return;
    }

    try {
      const response = await axios.put(
        `http://localhost:8000/usuarios/restablecer/${token}`,
        { nuevaContrasena }
      );
      setMensaje(response.data.message);
      setTimeout(() => navigate('/login'), 3000); // Redirige al login después de 3 segundos
    } catch (error) {
      setError(error.response?.data?.message || 'Error al actualizar la contraseña');
    }
  };

  if (!isTokenValid) {
    return <div className="text-center mt-10 text-red-600">{error}</div>;
  }

  return (
    <div
      className="max-w-lg mx-auto mt-10 p-8"
      style={{
        background: "linear-gradient(135deg, #fef6e4, #f7d9c4)",
        borderRadius: "15px",
        boxShadow: "0px 8px 20px rgba(0, 0, 0, 0.2)",
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <div style={{ textAlign: "center", marginBottom: "30px" }}>
        <h2
          style={{
            color: "#8b5d33",
            fontWeight: "bold",
            fontSize: "24px",
          }}
        >
          Restablecer Contraseña
        </h2>
        <p
          style={{
            fontSize: "14px",
            color: "#6c5240",
            marginTop: "5px",
          }}
        >
          Ingresa tu nueva contraseña y confírmala para poder restablecer tu acceso. Asegúrate de que ambas contraseñas coincidan.
        </p>
      </div>
      
      <form
        onSubmit={handleSubmit}
        className="space-y-6"
        style={{
          display: "flex",
          flexDirection: "column",
          gap: "20px",
        }}
      >
        <div>
          <label htmlFor="nuevaContrasena" className="block text-sm font-medium text-gray-700">
            Nueva Contraseña
          </label>
          <input
            type="password"
            id="nuevaContrasena"
            value={nuevaContrasena}
            onChange={(e) => setNuevaContrasena(e.target.value)}
            className="mt-1 block w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 shadow-sm"
            style={{
              padding: "12px",
              border: "1px solid #d4c4b5",
              borderRadius: "8px",
              fontFamily: "'Poppins', sans-serif",
              boxShadow: "inset 0px 2px 4px rgba(0, 0, 0, 0.1)",
            }}
            required
          />
        </div>
  
        <div>
          <label htmlFor="confirmarContrasena" className="block text-sm font-medium text-gray-700">
            Confirmar Contraseña
          </label>
          <input
            type="password"
            id="confirmarContrasena"
            value={confirmarContrasena}
            onChange={(e) => setConfirmarContrasena(e.target.value)}
            className="mt-1 block w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 shadow-sm"
            style={{
              padding: "12px",
              border: "1px solid #d4c4b5",
              borderRadius: "8px",
              fontFamily: "'Poppins', sans-serif",
              boxShadow: "inset 0px 2px 4px rgba(0, 0, 0, 0.1)",
            }}
            required
          />
        </div>
  
        <button
          type="submit"
          className="w-full py-3 px-4 rounded-md bg-indigo-600 text-white font-medium text-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
          style={{
            padding: "12px",
            background: "linear-gradient(90deg, #e6af81, #d89261)",
            fontWeight: "bold",
            fontSize: "16px",
            cursor: "pointer",
            transition: "transform 0.3s ease",
          }}
          onMouseOver={(e) => (e.target.style.transform = "scale(1.05)")}
          onMouseOut={(e) => (e.target.style.transform = "scale(1)")}
        >
          Restablecer Contraseña
        </button>
      </form>
  
      {/* Mensajes */}
      {mensaje && (
        <p
          style={{
            marginTop: "20px",
            color: "green",
            textAlign: "center",
            fontSize: "14px",
          }}
        >
          {mensaje}
        </p>
      )}
      {error && (
        <p
          style={{
            marginTop: "20px",
            color: "red",
            textAlign: "center",
            fontSize: "14px",
          }}
        >
          {error}
        </p>
      )}
    </div>
  );
  
  
};

export default RestablecerContrasena;
