import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ConfiguracionPerfil.css'; 

const ConfiguracionPerfil = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  
  useEffect(() => {
    // Llamar al backend para obtener el perfil actual
    const fetchProfile = async () => {
      try {
        // Obtener el ID del usuario y el token del almacenamiento local
        const id_usuario = localStorage.getItem('id_usuario');
        const token = localStorage.getItem('token');

        const response = await axios.get(`http://localhost:8000/perfil/${id_usuario}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          }
        });
        console.log(response); // Log de la respuesta
        

        setNombre(response.data.usuario.nombre);
        setEmail(response.data.usuario.email);
      } catch (err) {
        setError('Error al cargar el perfil');
      }
    };
  
    fetchProfile();
  }, []); // Se ejecuta solo una vez cuando se monta el componente
  
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const id_usuario = localStorage.getItem('id_usuario');
      const token = localStorage.getItem('token');
  
      // Enviar la solicitud PUT para actualizar el perfil
      const response = await axios.put(`http://localhost:8000/perfil/${id_usuario}`, 
      { nombre, email }, {
        headers: {
          'Authorization': `Bearer ${token}`, // Incluir el token de autorización en la cabecera
        }
      });

      // Mostrar mensaje de éxito
      setSuccessMessage(response.data.message);
    } catch (err) {
      setError('Error al actualizar el perfil: ' + err.message);
    }
  };

  return (
    <div className="configuracion-perfil-container">
      <h2>Configuración del Perfil</h2>
      
      <div className="perfil-card">
        <form className="perfil-formulario" onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Nombre</label>
            <input 
              type="text" 
              value={nombre} 
              onChange={(e) => setNombre(e.target.value)} 
              placeholder="Tu nombre" 
            />
          </div>
          <div className="form-group">
            <label>Correo Electrónico</label>
            <input 
              type="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              placeholder="tucorreo@ejemplo.com" 
            />
          </div>
          <button type="submit" className="btn-guardar">Guardar Cambios</button>
        </form>

        {/* Mostrar errores o mensajes de éxito */}
        {error && <p className="error-message">{error}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}
      </div>
    </div>
  );
};

export default ConfiguracionPerfil;
