import axios from 'axios'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'

const URI = 'http://localhost:8000/usuarios'

const CompShowUsuarios = () => {
    const [usuarios, setUsuarios] = useState([])

    useEffect(() => {
        getUsuarios()
    }, [])

    const getUsuarios = async () => {
        try {
            const res = await axios.get(URI, {
                headers: {
                    "Authorization": `Bearer ${localStorage.getItem("token")}`
                }
            });
            setUsuarios(res.data);
        } catch (error) {
            console.error("Error al obtener los usuarios:", error);
            if (error.response) {
                // Esto ayudará a detectar errores del servidor como 403
                console.log("Código de estado:", error.response.status);
                console.log("Mensaje de error:", error.response.data);
                if (error.response.status === 403) {
                    alert("No tienes permiso para acceder a esta página.");
                    // Puedes redirigir al usuario a la página de inicio de sesión si lo deseas
                    // window.location.href = '/login';
                }
            } else {
                console.error("Error de solicitud:", error.message);
            }
        }
    };
   

    const deleteUsuario = async (id_usuario) => {
        const token = localStorage.getItem("token");
        try {
            await axios.delete(`${URI}/${id_usuario}`, {
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            });
            getUsuarios();
        } catch (error) {
            console.error("Error al eliminar el usuario:", error);
            if (error.response) {
                console.log("Código de estado:", error.response.status);
                console.log("Mensaje de error:", error.response.data);
            }
        }
    }

    return (
        <div className='container mt-5'>
            <div className='row'>
                <div className='col'>
                    <Link to="/create" className='btn btn-primary mb-3' style={{ backgroundColor: '#007bff', color: 'white' }}>Crear Usuario</Link>
                    <table className='table table-hover table-bordered' style={{ backgroundColor: 'white' }}>
                        <thead className='text-center' style={{ backgroundColor: '#8B4513', color: '#FFFFFF' }}>
                            <tr>
                                <th>Nombre</th>
                                <th>Email</th>
                                <th>Acciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            {usuarios.map((usuario) => (
                                <tr key={usuario.id_usuario}>
                                    <td>{usuario.nombre}</td>
                                    <td>{usuario.email}</td>
                                    <td className="text-center">
                                        <div className="btn-group" role="group">
                                            <Link to={`/edit-usuario/${usuario.id_usuario}`} className='btn btn-success btn-sm' style={{ backgroundColor: '#28a745', color: 'white', marginRight: '5px' }}>Editar</Link>
                                            <button onClick={() => deleteUsuario(usuario.id_usuario)} className='btn btn-danger btn-sm' style={{ backgroundColor: '#dc3545', color: 'white' }}>Eliminar</button>
                                        </div>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}

export default CompShowUsuarios
